import React, { Component } from 'react'
import './formstyle.css'
import Logo from './images/logo_2.jpg'

 class Formcomponent extends Component {
     constructor(props) {
         super(props)

         this.state = {
              username:''
         }
     }

     handlechange(event){
        this.setState({
            username
        })
     }
    check(){
            var get=document.getElementById("password");
            if(get.value.trim().length<5){
                alert("Password is short must be greater than 5");
                return false;
            }
        } 
    render() {
        return (
            <div>
                <div className="box" >
                <div className="box__animation"> 
                {/* <center><div className="box__bg"></div></center> */}
                <center> <img src={Logo} /></center>
              <center><h2><b>Log in to InstaTweets</b></h2></center> 
                <form >
               <div className="labelstyle"><label>Phone, email,or username</label></div>
            <div className="inputstyle"><input type="text" class="form-control" value={this.state.username} required></input></div> 
                    <br></br>

                   <div className="labelstyle"> <label >Password</label></div>
                   <div className="inputstyle"><input type="password" class="form-control" id="password" required></input> </div>  

                    < button type="submit" onClick={this.check} onChange={this.handlechange} className="btn btn--white">Log in</button> 
                    <br></br>
                    <br></br>
                    <span ><a href="#">Forgot password?.</a></span>  
                    <span className="fix"><a href="#">Sign up for instatweets?.</a></span>
                </form>

                </div>
                </div> 
            </div>
        )
    }
}

