import React, { Component } from 'react'
import './Userprofile.css'
// import Logo from './images/logo.jpg'
import axios from 'axios'
//import history from '../history'
import Usericon from "./images/User_icon.jpg"
import Usericon2 from "./images/User_icon2.jpg"
import Small_logo from "./images/Small_logo.jpg"
import "./Mainpage.css"
import history from '../history'
import TwitterLogo from  "./images/twitter.png"

export class UserProfile extends Component {

    constructor(props) {
        super(props)

        this.state = {
             first_name:"",
             last_name:"",
             username:"",
             error_Message:"",
             user_bio:"",   
             following_count:"",
             followers_count:"",
             token:localStorage.getItem("token"),
             tweets:[],


        }



    }


    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })
    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    changeUserBio=(event)=>{
        this.setState({
            user_bio:event.target.value
        })
    }
    changeFollow=(event)=>{
        this.setState({
            following_count:event.target.value
        })
    }
    changeFollowers=(event)=>{
        this.setState({
            followers_count:event.target.value
        })
    }
    componentDidMount(){

        axios
        .post("http://127.0.0.1:8000/profile/",this.state)
        .then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({first_name:response.data["first_name"],
                           last_name:response.data["last_name"],
                           username:response.data["username"],
                           user_bio:response.data["user_bio"],
                           following_count:response.data["following_count"],
                           followers_count:response.data["followers_count"]


           })

        }


    }).catch(error=>{
       if(error.response.status="400"){
            this.setState({error_Message:error.response.data["message"]});
            console.log(error.response);
        }

    })
        if(this.state.token!=null){
            console.log(this.state.token);
        }
        else{
            console.log("not present");
        }




    }


    removeitem(){
        localStorage.removeItem("token");
    }



    render() {
        const {first_name,last_name,username,error_Message,user_bio,following_count,followers_count,tweets}=this.state
        return (
            <div>
            <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followeusersbutton"  onClick={()=>{history.push("/Follow")}}><i class="fa fa-list"></i>Follow Users</button>
            <br/>
            <br/>
            <button className="change followersbutton" onClick={()=>{history.push("/Followers")}}><i class="fa fa-user-friends"></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton" onClick={()=>{history.push("/Followings")}}><i class="fa fa-user-plus" ></i>Following</button>
            <br/>
            <br/>
            <button className="change profilebutton"onClick={()=>{history.push("/UserProfile")}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets")}>Tweet</button>
            </div>  
            <div className="rightpart">
        <div className="homestyle">{first_name} {last_name}</div> 
            <div className="BottomPart"> </div>
             <div className="graydiv"></div>
             <div class="circle"><img src={Usericon} className="userstyle"></img></div>

           <div className="showprofilediv">

        <div onChange={this.changeFirstName} onChange={this.changeLastName} className="firstName_lastName"><text >{first_name} {last_name}</text></div> <br/>
              <div onChange={this.changeUserName} ><text className="user">@{username}</text></div> <br/>
             <div  onChange={this.changeUserBio} ><text className="userbio">UserBio: {user_bio}</text></div> <br/>
            <div  onChange={this.changeFollowers} ><text className="followersCount">{followers_count} Followers</text></div> <br/>
            <div  onChange={this.changeFollow} > <text className="followingCount">{following_count} Following</text></div><br/>
           <center><button type="button" className="biobutton" onClick={()=>history.push("/Edit")} >Edit Bio</button><br/></center>
           <button type="button"  className="buttonstyle" onClick={this.removeitem}  >LogOut</button>

               {/* <a id="anchortag" style={{visibility:"hidden"}} onClick={()=>{history.push("/Login")}}>Login</a> */}

             </div>
                <div><strong>{error_Message}</strong></div>

             </div>  
             </div>
        )
    }
}

export default UserProfile
