import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import "./timeline.css"
import history from '../history'
import TwitterLogo from  "./images/twitter.png"

 class timeline extends Component {
    constructor(props) {
        super(props)
        this.state = {
             token:localStorage.getItem("token"),
            // token2:localStorage.getItem("token"),
            userdata:[],            
             Content:[],
             Error:"",
             username:"",
             error_message:"",
             message:"",
             content:"",
             query:"",
             //follow:"",
        }
    }

    Tweetcreate=()=>{
        axios.post("http://127.0.0.1:8000/tweets/create/",this.state).then(response=>{
            console.log(response);
        }).catch(e=>{
            if(e.response.status="400"){
                this.setState({message:e.response.data["message"]})
            }
              console.log(e.response);
        })
    }
    tweetwrite=(event)=>{
        this.setState({content:event.target.value})
    }
    componentDidMount(){

        axios.post("http://127.0.0.1:8000/tweets/timeline/",this.state).then(response=>{
            console.log(response);
            if(response.status="200"){
                this.setState({Content:response.data})
            }
            if(response.status="204"){
                this.setState({error_message:"Zero Tweets Found"})

            }

        }).catch(error=>{
            if(error.response.status="401"){
                this.setState({Error:error.response.data["message"]})
            }

        })
    }
    changeserach=(event)=>{
        this.setState({query:event.target.value})
    }
    searchapi=()=>{
        axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
            console.log(response);

            if(response.status="200"){

                const userarray=response.data;
                console.log(userarray);

                this.setState({userdata:userarray})

                console.log(this.state.userdata);



            }


        }).catch(error=>{
            console.log(error.response);
             this.setState({error_message:error.response.data["message"]})
        })
        console.log(this.state.token);
    }
    followusers=(followfield,nameuser)=>{
        const obj={
            follow:followfield,
            username:nameuser,
            token:this.state.token
        }
        if(obj.follow==1){
            axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
                console.log(response.data)
            }).catch(error=>{

                console.log(error.response)
            })
          var taketheclassName= document.getElementsByClassName("textchange")
          for(var i=0;i<get.length;i++){
              get[i].innerHTML="f"
          }
        }
        else{
            axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
                console.log(response);
            }).catch(error=>{
                console.log(error.response);
            })
           var get2= document.getElementsByClassName("textchange")
            for(var i=0;i<get2.length;i++){
                get[i].innerHTML="t"
            }
        }
    }
    removeitem(){
        localStorage.removeItem("token");
    }

    render() {
        const {Content,Error,error_message,username,message,content,query,userdata,follow}=this.state
        return (
            <div>
                <div className="leftpart">

            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}} ><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followersbutton" onClick={()=>{history.push("/Followers")}} ><i class="fa fa-user-friends"></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton" onClick={()=>{history.push("/Followings")}}><i class="fa fa-user-plus"></i>Following</button>
            <br/>
            <br/>
            <button className="change profilebutton" onClick={()=>{history.push("/UserProfile")}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>{history.push("/Tweets")}}>Tweet</button>
            <button type="button"  className="buttonstyle" onClick={this.removeitem}  >LogOut</button>
            </div> 
            <div className="rightpart">

            <div className="homestyle">Home</div>
            <div className="BottomPart"> </div>
           <div><i class="fa fa-user-circle"></i> </div> 
           <textarea value={content} onChange={this.tweetwrite}  className="textareastyle" placeholder="What's happening" ></textarea> 
           <button onClick={this.Tweetcreate} className="timelinetweetbtn" type="submit"  >Tweet</button>
           <div className="error_message">{message} </div> 
            <div className="BottomPart2">  </div>

            <br/>


          <div className="timeline">{Content.length? Content.map(con=> <div key={con.id} className="Tofocus" >
          <div className="timelineuserstyle"><i class="fa fa-user-circle"></i> </div><div className="timelinetext1">
            <a className="anchor" onClick={()=>history.push("/UserProfile")}>{con.full_name} @{con.user_name}</a>  </div> <div className="contentstyle">{con.content}</div>
           <div className="bottomline"></div>
           </div>
           ):null}
           </div>

     </div> 
          <i class="fa fa-search" area-hidden="true"></i>  
          <input type="text" value={query} onChange={this.changeserach} className="searchbox" placeholder="Search Mini-twitter" ></input>

          <button className="Searchbtn" onClick={this.searchapi}>Search</button>
          <br/><br/><br/><br/>
          <div className="search_div">{userdata.length ? userdata.map (user=><div key={user.id}><div className="timelineuserstyle2"><i class="fa fa-user-circle"></i> </div><div className="timelinetext1">{user.first_name} {user.last_name}</div>
        <div className="search_username">@{user.username}
       <div className="changebtn"> <button onClick={()=>this.followusers(user.follow,user.username)} className="textchange">Follow</button></div>
      <hr className="searchHr"></hr></div></div>):<div>NO Search</div>} </div>
          <br/><br/><br/>


            </div>
        )
    }
}

export default timeline
