import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import './tweet.css'
import history from '../history'
import TwitterLogo from  "./images/twitter.png"

export class Tweets extends Component {
    constructor(props) {
        super(props)
        this.state = {
            token:localStorage.getItem("token"),
            content:"",
            tweets:[],
            username:"",
            message:"",
            message2:"",
            first_name:"",
            last_name:"",
            userdata:[],
            query:""



        }
    }

    usernamechange=(event)=>{
        this.setState({username:event.target.value})
    }
    tweetcreate=()=>{
        axios.post("http://127.0.0.1:8000/tweets/create/",this.state).then(response=>{
            console.log(response);
        }).catch(e=>{
            if(e.response.status="400"){
                this.setState({message:e.response.data["message"]})
            }
              console.log(e.response);
        })
    }
    showtweets=(event)=>{
        this.setState({
            content:event.target.value
        })

    }


    componentDidMount(){
        axios.post("http://127.0.0.1:8000/profile/",this.state).then(response=>{
            if(response.status="200"){
                this.setState({username:response.data["username"]})
                this.setState({first_name:response.data["first_name"]})
                this.setState({last_name:response.data["last_name"]})
            }
        })
        axios.post(" http://127.0.0.1:8000/tweets/",this.state).then(response=>{
      console.log(response);
      if(response.status="200"){
          this.setState({tweets:response.data});
      }
    //   if(response.status="204"){
    //       this.setState({message2:"zero tweets found"})
    //   }

        }).catch(error=>{
            if(error.response.status="204"){
                this.setState({message2:"Zero Tweets Found"})
            }
            if(error.response.status="400")
            this.setState({message:error.response.data["message"]})
                console.log(error.response);
        })

    }
    searchapi=()=>{
        axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
            console.log(response);

            if(response.status="200"){

                const userarray=response.data;
                console.log(userarray);

                this.setState({userdata:userarray})

                console.log(this.state.userdata);



            }


        }).catch(error=>{
            console.log(error.response);
             this.setState({error_message:error.response.data["message"]})
        })
        console.log(this.state.token);
    }

    followusers=(followfield,nameuser)=>{
        const obj={
            follow:followfield,
            username:nameuser,
            token:this.state.token
        }
        if(obj.follow==1){
            axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
                console.log(response.data)
            }).catch(error=>{

                console.log(error.response)
            })
           document.getElementById("textchange").innerHTML="f"
        }
        else{
            axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
                console.log(response);
            }).catch(error=>{
                console.log(error.response);
            })
            document.getElementById("textchange").innerHTML="t" 
        }
    }
    render() {
        const {content,tweets,message,username,message2,first_name,last_name}=this.state
        return (
            <div>
            <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followeusersbutton"  onClick={()=>{history.push("/Follow")}}><i class="fa fa-list"></i>Follow Users</button>
            <br/>
            <br/>
            <button className="change followersbutton"><i class="fa fa-user-friends" onClick={()=>{history.push("/Followers")}}></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton"><i class="fa fa-user-plus" onClick={()=>{history.push("/Followings")}}></i>Following</button>
            <br/>
            <br/>
            <button className="change profilebutton" onClick={()=>{history.push("/UserProfile")}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton">Tweet</button>
            </div>  
            <div className="rightpart">
            <div className="homestyle">{first_name} {last_name}</div>
         <div className="DF_username">{username}</div>
         <div className="BottomPart"> </div>


            {/* <input  className="styleofinput" type="text" value={content} onChange={this.showtweets}  placeholder={username}></input><br/>
            <div className="messagestyling">{message}</div> */}
             <div><i class="fa fa-user-circle"></i> </div> 
               <textarea value={content} onChange={this.showtweets} className="tweettextarea" placeholder="What's happening"></textarea> 
              <br/>
            <button className="tweet_tweetbtn" type="submit" onClick={this.tweetcreate}  >Tweet</button>
            <div className="error_message">{message} </div> 
            <br/>
            <br/>
           <div className="BottomPart2">  </div>
        {/* <div className="outerdiv">
        {tweets.length? tweets.map(tweet=> <div key={tweet.id} id="size" className="tweetstext1">
            <div className="usernamestyle">{first_name}{last_name}@{username}
            </div>{tweet.content}<div id="size2" className="tweetstext2"> 
            {tweet.created_date} </div><hr class="hrstyle1"></hr></div>):null}</div> */}
        <div className="timeline">
        {tweets.length? tweets.map(tweet=><div key={tweet.id} className="Tofocus" >
        <div className="timelineuserstyle"><i class="fa fa-user-circle"></i> </div><div className="timelinetext1">{first_name} {last_name}  @{username}</div> 
       <div className="contentstyle">{tweet.content}</div><div className="bottomline"></div></div>):null}</div>





        <div className="messagestyling">{message2}</div>
      </div>    

            </div>
        )
    }
}

export default Tweets
