import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import history from '../history'
import "./showfollowings.css"
export class DisplayFollowings extends Component {
    constructor(props) {
        super(props)

        this.state = {
             token:localStorage.getItem("token"),
             first_name:"",
             last_name:"",
             username:""
        }
    }
    displayfollowings=()=>{
        axios.post("http://127.0.0.1:8000/following/",this.state).then(resposnse=>{
            console.log(resposnse)
            if(resposnse.status="200"){
                this.setState({first_name:resposnse.data.followings["first_name"]})
                this.setState({last_name:resposnse.data.followings["last_name"]})
                this.setState({username:resposnse.data.followings["username"]})
            }
        }).catch(error=>{
            console.log(error.resposnse);
        })
    }

    render() {
        return (
            <div>
                <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <br></br>
            <br></br>
            <button className="change followersbutton"><i class="fa fa-user-friends"></i>Followers</button>
            <br></br>
            <br></br>
            <button className="change followingbutton"><i class="fa fa-user-plus"></i>Following</button>
            <br></br>
            <br></br>
            <button className="change profilebutton"><i class="fa fa-user"></i>Profile</button>
            <br></br>
            <br></br>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets")}>Tweet</button>
            </div>  
            <div className="rightpart">
              <h2 className="styleOfh2">InstaTweets</h2>
              <div className="styleofimg"><img src={Small_logo}></img></div>
             <div style={{height:"0px",width:"102%",borderBottom:"2px solid black",marginTop:"4px",marginLeft:"-1%"}}></div>
        <div onChange={this.firstnamechange}>{}</div><div onChange={this.firstnamechange}>{}</div>
            <center><button onClick={this.displayfollowings} className="followbut">Display Followings</button></center> 
            </div>

            </div>
        )
    }
}

export default DisplayFollowings
