import Usericon from "./images/User_icon.jpg"
import Usericon2 from "./images/User_icon2.jpg"
import Small_logo from "./images/Small_logo.jpg"
import "./Mainpage.css"
import history from '../history'
import SideBar from './SideBar'
import UniversalSearch from './UniversalSearch'

class UserProfile extends Component {

    constructor(props) {
        super(props)

        this.state = {
             error_Message:"",
             bio:"",   
             token:localStorage.getItem("token"),
             UserInfo:"",
             followers:"",
             following:"",
             q:"",
             Searchdata:[],
             UsersTweets:[],
             id:localStorage.getItem("id")


        }
        console.log("UserPROFILE",this.props)


    }



    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    changeUserBio=(event)=>{
        this.setState({
            bio:event.target.value
        })
    }


    componentDidMount(){
        const {id}=this.state

    //     const username=this.props.match.params.username
    //     axios
    //     .post("http://127.0.0.1:8000/profile/",{"username":username,"token":this.state.token})
    //     .then(response=>{
    //     console.log(response);
