import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import history from '../history'
import "./showfollowings.css"
import TwitterLogo from  "./images/twitter.png"
export class DisplayFollowings extends Component {
    constructor(props) {
        super(props)

        this.state = {
             token:localStorage.getItem("token"),
             first_name:"",
             last_name:"",
             username:"",
             error_message:"",
             followings:[],
             error_message2:"",
             userdata:[],
             query:""
        }
    }
    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })

    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    componentDidMount(){

    //     axios
    //     .post("http://127.0.0.1:8000/profile/",this.state)
    //     .then(response=>{
    //     console.log(response);
    //     if(response.status="200"){
    //         this.setState({first_name:response.data["first_name"],
    //                        last_name:response.data["last_name"],
    //                        username:response.data["username"],
    //        })

    //     }


    // }).catch(error=>{
    // //    if(error.response.status="400"){
    // //         this.setState({error_Message:error.response.data["message"]});
    // //         console.log(error.response);
    // //     }

    // })

        axios.post("http://127.0.0.1:8000/following/",{token:this.state.token}).then(resposnse=>{
            console.log(resposnse)
            if(resposnse.status="200"){

               this.setState({followings:resposnse.data.followings})


            }

        }).catch(error=>{
            // if(error.resposnse.status="400"){
            //     this.setState({error_message:error.resposnse.data["message"]})
            // }
            // if(error.resposnse.status="401"){
            //     this.setState({error_message:error.resposnse.data["message"]})
            // }
            console.log(error.resposnse)

        })

    }
    changeserach=(event)=>{
        this.setState({query:event.target.value})
    }
    searchapi=()=>{
        axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
            console.log(response);

            if(response.status="200"){

                const userarray=response.data;
                console.log(userarray);

                this.setState({userdata:userarray})

                console.log(this.state.userdata);



            }


        }).catch(error=>{
            console.log(error.response);
             this.setState({error_message:error.response.data["message"]})
        })
        console.log(this.state.token);
    }
   unfollow=(unfollouser,event)=>{
    const obj={
        token:this.state.token,
        username:unfollouser
    }
       axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
           console.log(response.data)
       }).catch(error=>{

           console.log(error.response)
       })

      // event.preventDefault();
   }
   followusers=(followfield,nameuser)=>{
    const obj={
        follow:followfield,
        username:nameuser,
        token:this.state.token
    }
    if(obj.follow==1){
        axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
            console.log(response.data)
        }).catch(error=>{

            console.log(error.response)
        })
        var SelectClass= document.getElementsByClassName("textchange")
        for(var i=0;i<SelectClass.length;i++){
          SelectClass[i].innerHTML="Unfollow"
        }
    }
    else{
        axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
            console.log(response);
        }).catch(error=>{
            console.log(error.response);
        })
        var SelectClass2= document.getElementsByClassName("textchange")
        for(var i=0;i<SelectClass2.length;i++){
          SelectClass2[i].innerHTML="Follow"
        }
    }
}

    showprofile(username){

        {history.push(("/UserProfile/"+username));
     }
    }
   hoverchange(){
      var tagbtn= document.getElementsByClassName("btnchange");
      for(var i=0;i<tagbtn.length;i++){
          tagbtn[i].innerHTML="Unfollow";
          tagbtn[i].style.backgroundColor="#DF6240";

      }
    //    document.getElementsByTagName("button").style.backgroundColor="#DF6240 ";
       return false;
   }
   hoverout(){

      var tagbtn1= document.getElementsByClassName("btnchange");
      for(var i=0;i<tagbtn1.length;i++){
        tagbtn1[i].innerHTML="Following";
         tagbtn1[i].style.backgroundColor="#4ea7f0";
      }
       //document.getElementsByTagName("button").style.backgroundColor="#4ea7f0 ";
       return false;
   }
   removeitem(){
    localStorage.removeItem("token");
    {history.push("/Login")}
}
