            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets")}>Tweet</button>
            <button type="button"  className="buttonstyle" onClick={this.removeitem}  >LogOut</button>
            </div>  
            <div className="rightpart">

        <div className="homestyle">{first_name} {last_name}</div>
        <div className="DF_username">{username}</div>

        <button className="DF_followingsbtn" >Followings</button>
        <hr className="DE_hr"></hr>

            {/* {followings.map(follow=><div key={follow.id} className="styleoffirst_name">{follow.first_name}<div className="styleoflast_name">{follow.last_name} */}
            {/* <div className="styleofusername">@{follow.username}</div> */}
            {/* <button className="unfollowstyle" onClick={()=>{this.unfollow(follow.username)}}>Unfollow</button></div><hr className="styleof_hr"></hr></div></div>)} */}

        { followings.length ? followings.map(follow=><div key={follow.id} className="Tofocus"> <div className="timelineuserstyle"> <i class="fa fa-user-circle"></i></div>
        <a className="anchor" onClick={()=>this.showprofile(follow.username)}>

            <div className="DF_firstName">{follow.first_name} {follow.last_name}</div></a>
        <div className="DF_user_name">@{follow.username}</div>
        <button className="btnchange"  onMouseOver={this.hoverchange} onMouseOut={this.hoverout} 
        onClick={()=>this.unfollow(follow.username)} >Following</button>
       <div className="bottomline"></div></div>):<center><h2>Zero Followings</h2></center>}



           </div>
           <i class="fa fa-search" area-hidden="true"></i>  
          <input type="text" value={query} onChange={this.changeserach} className="searchbox" placeholder="Search Mini-twitter" ></input>

          <button className="Searchbtn" onClick={this.searchapi}>Search</button>
          <br/><br/><br/><br/>
          <div className="search_div">{userdata.map (user=><div key={user.id}><div className="timelineuserstyle">
              <i class="fa fa-user-circle"></i> </div><div className="timelinetext1">{user.first_name} {user.last_name}</div>
        <div className="search_username">@{user.username}

        <div><button onClick={()=>this.followusers(user.follow,user.username)} 
        className="textchange">{user.follow==1?"Unfollow":"Follow"}</button></div><hr className="searchHr"></hr>
       </div></div>)}</div> 

            {error_message}

            </div>


        )
    }
}

export default DisplayFollowings
