import React, { Component } from 'react'
import axios from 'axios'

export class Tweets extends Component {
    constructor(props) {
        super(props)
        this.state = {
            token:localStorage.getItem("token"),
            content:""

        }
    }
    tweetcreate=()=>{
        axios.post("http://127.0.0.1:8000/tweets/create/",this.state).then(response=>{
            console.log(response);
        }).catch(e=>{
              console.log(e.response);
        })
    }
    showtweets=(event)=>{
        this.setState({
            content:event.target.value
        })

    }
    displaytweets=()=>{
        axios.post(" http://127.0.0.1:8000/tweets/",this.state).then(response=>{
      console.log(response);
      if(response.status="200"){
          this.setState({tweets:response.data});
      }
        }).catch(error=>{
            console.log(error.response);
        })
    }
    render() {
        const {content}=this.state
        return (
            <div>
             <input type="text" value={content} onChange={this.showtweets}></input>
             <br></br>
             <br></br>
            <button type="submit" onClick={this.tweetcreate}>Tweet</button>
            <button type="button" onClick={this.displaytweets}></button>
            </div>
        )
    }
}

export default Tweets
