import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import "./edit.css"
import history from '../history'
import TwitterLogo from  "./images/twitter.png"
import SideBar from './SideBar'
import UniversalSearch from './UniversalSearch'

class Edit extends Component {
    constructor(props) {
        super(props)

        this.state = {
             bio:"",
             user_bio:"",
             username:"",
             savemessage:"",
             error_message:"",
             userdata:[],
             UserInfo:""

        }
    }
    componentDidMount(){
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token") 
        }

           axios.get("http://127.0.0.1:8000/user/profile/").then(response=>{
               if(response.status="200"){
                   this.setState({
                       UserInfo:response.data["user"],
                       bio:response.data["bio"],
                       followers:response.data["followers"],
                       following:response.data["following"]

                    })
                    const storeId=response.data.user["id"];
                    localStorage.setItem("id",storeId);

               }

               console.log(response)
           }).catch(error=>{
               console.log(error.response);
           })


    }
    savedata=()=>{
        const storeid=localStorage.getItem("id")
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token") 
        }
     axios.patch("http://127.0.0.1:8000/user/profile/",{"bio":this.state.bio}).then(response=>{
         console.log(response);
         this.setState({savemessage:response.data["message"]})

     }).catch(error=>{
         console.log(error.response);
         this.setState({error_message:error.response.data["message"]})
     })
     console.log(this.state.token);
    }

    userbiochange=(event)=>{
        this.setState({bio:event.target.value})
    }
    removeitem(){
        localStorage.removeItem("token");
        {history.push("/Login")}
    }
    render() {
        const{first_name,last_name,bio,username,savemessage,error_message,query,userdata,UserInfo}=this.state
        return (
            <div>
           <SideBar></SideBar>
            <div className="rightpart">
            <div className="homestyle">{UserInfo.first_name} {UserInfo.last_name}</div>
         <div className="DF_username">{UserInfo.username}</div>
         <div className="BottomPart"> </div>

            <div>
            <div className="editheading">Edit Profile </div>
            <button type="submit" onClick={this.savedata} className="savebutton" >Save</button>
            <div className="BottomPart"> </div>
              <form className="editform">
                 <div className="labelstyle1"><label>First Name</label></div>
              <div className="inputstyle1">  <input type="text"  value={UserInfo.first_name}  placeholder="Change FirstName" required ></input></div>
                <br/>
                <br/>
                <div className="labelstyle1"><label>Last Name</label></div>
              <div className="inputstyle1"> <input type="text"  value={UserInfo.last_name} placeholder="Change LastName" required></input></div> 
                <br/>
                <br/>
                <div className="labelstyle1"><label>Bio</label></div>
               <div className="inputstyle1"> <input type="text"  value={bio} onChange={this.userbiochange} placeholder="Change UserBio" required></input></div>
                <br/>
                <br/>


             <div className="messagestyling">{error_message}</div>
                </form>
            </div>
            </div>
            <UniversalSearch></UniversalSearch>
            </div>
        )
    }
}

export default Edit
