import React, { Component } from 'react'
import axios from 'axios'

export class Tweets extends Component {
    constructor(props) {
        super(props)
        this.state = {
            token:localStorage.getItem("token"),
            content:"",
            tweets:[],
            config:[]

        }
    }
    tweetcreate=()=>{
        axios.post("http://127.0.0.1:8000/tweets/create/",this.state).then(response=>{
            console.log(response);
        }).catch(e=>{
              console.log(e.response);
        })
    }
    showtweets=(event)=>{
        this.setState({
            content:event.target.value
        })

    }
    displaytweets=()=>{
        axios.post(" http://127.0.0.1:8000/tweets/",this.state).then(response=>{
      console.log(response);
      if(response.status="200"){
          this.setState({tweets:response.data});
      }
      this.setState({
          config:response.c
      });
        }).catch(error=>{
            console.log(error.response);
        })
    }
    render() {
        const {content,tweets,config}=this.state
        return (
            <div>
             <input type="text" value={content} onChange={this.showtweets}></input>
             <br></br>
             <br></br>
            <button type="submit" onClick={this.tweetcreate}>Tweet</button>

            <button type="button" onClick={this.displaytweets}>Display</button>
        <div>@{config.map(con=><div key={config.username}>{config.username}</div>)}</div> {tweets.length? tweets.map(tweet=> <div key={tweet.id}>{tweet.content}<div>{tweet.created_date}</div></div>):null}
            </div>
        )
    }
}

export default Tweets
