import React, { Component } from 'react'
import axios from 'axios'

export class UniversalSearch extends Component {
    constructor(props) {
        super(props)

        this.state = {
            Searchdata:[],
             search:""
        }
    }
    changeserach=(event)=>{
        this.setState({search:event.target.value})
    }


    search=()=>{
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token") 
        }
        axios.get("http://127.0.0.1:8000/users/",{params:{search:this.state.query}}).then(response=>{
             this.setState({Searchdata:response.data})

            console.log(this.state.Searchdata);

        }).catch(error=>{
            console.log(error.response)
        })
    }
    followusers=(followfield,userid)=>{
        const obj={
            follow:followfield,
            id:userid
        }
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token") 
        }
        const storeid=localStorage.getItem("id")
        console.log(obj.id);
        console.log(obj.follow);
        if(obj.follow==false){ 
            axios.post("http://127.0.0.1:8000/user/"+storeid+"/follow/"+obj.id).then(response=>{
                console.log(response)
            }).catch(error=>{
                console.log(error.response);
            })
        }

    }
    render() {
        const{Searchdata,query}=this.state
        return (
            <div>
                   <i class="fa fa-search" area-hidden="true"></i> 
             <input value={query} onChange={this.changeserach} type="text" className="searchbox" placeholder="Search Mini-twitter"></input>

             <button className="Searchbtn" onClick={this.search} type="submit" >Search</button>
             <br/><br/><br/><br/>
   <div  className="search_div">{Searchdata.length? Searchdata.map(Search=><div key={Search.id}>
       <div className="timelineuserstyle2"><i class="fa fa-user-circle"></i> </div>
       <div className="timelinetext1">{Search.first_name}{Search.last_name}</div>
   <div className="search_username">@{Search.username}</div>
        <div> <button onClick={()=>this.followusers(Search.follow,Search.id)} className="textchange">
            Follow</button></div><hr className="searchHr"></hr>
        </div>):"No Search"}</div>
            </div>
        )
    }
}

export default UniversalSearch
