import React, { Component } from 'react'
import history from '../history'
import TwitterLogo from  "./images/twitter.png"

export class SideBar extends Component {
    constructor(props) {
        super(props)

        this.state = {
            Searchdata:[],
             q:""
        }
    }
    changeserach=(event)=>{
        this.setState({q:event.target.value})
    }


    search=()=>{
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token") 
        }
        axios.get("http://127.0.0.1:8000/users/search/",{params:{q:this.state.q}}).then(response=>{
             this.setState({Searchdata:response.data})

            console.log(this.state.Searchdata);

        }).catch(error=>{
            console.log(error.response)
        })
    }

    render() {
        return (
            <div>
                <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followersbutton" onClick={()=>{history.push("/Followers")}}><i class="fa fa-user-friends"></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton" onClick={()=>{history.push("/Followings")}}><i class="fa fa-user-plus" ></i>Following</button>
            <br/>
            <br/>
            <button className="change profilebutton"onClick={()=>{history.push("/UserProfile/"+localStorage.getItem("username"))}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets")}>Tweet</button>
            <button type="button"  className="buttonstyle" onClick={this.removeitem}  >LogOut</button>
            </div>  

            <i class="fa fa-search" area-hidden="true"></i> 
             <input value={q} onChange={this.changeserach} type="text" className="searchbox" placeholder="Search Mini-twitter"></input>

             <button className="Searchbtn" onClick={this.search} type="submit" >Search</button>
             <br/><br/><br/><br/>
   <div  className="search_div">{Searchdata.length? Searchdata.map(Search=><div key={Search.id}>
       <div className="timelineuserstyle2"><i class="fa fa-user-circle"></i> </div>
       <div className="timelinetext1">{Search.first_name}{Search.last_name}</div>
         <div className="search_username">@{Search.username}</div>
        </div>):"No Search"}</div>
            </div>
        )
    }
}

export default SideBar
