import React, { Component } from 'react'
import history from '../history'
import TwitterLogo from  "./images/twitter.png"
import axios from "axios"
import Routes from '../Routes'


 class SideBar extends Component {
     constructor(props) {
         super(props)

         this.state = {
            Searchdata:[],
            search:"",
            //id:this.props.ID,
            id:"",

            error_message:""
         }
         console.log("SIDE",this.props)

     }
     changeserach=(event)=>{
        this.setState({search:event.target.value})
    }
    removeitem(){
        localStorage.removeItem("token");
        localStorage.removeItem("id");
        {history.push("/minitwitter/Login")}
    }
    search=()=>{
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token") 
        }
        axios.get("http://127.0.0.1:8000/minitwitter/users/",{params:{search:this.state.search}}).
        then(response=>{  
            console.log("Search",response);

            this.setState({Searchdata:response.data})
            console.log(this.state.Searchdata);

        }).catch(error=>{
            console.log(error.response)
        })
    }
    followusers=(followfield,userid)=>{
        const{id}=this.state
        const obj={
            follow:followfield,
            id:userid
        }
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token") 
        }
        console.log(obj.id);
        console.log(obj.follow);
        if(obj.follow==false){ 
        axios.post("http://127.0.0.1:8000/minitwitter/users/"+id+"/followings/",{"following_id":obj.id})
            .then(response=>{
                console.log("Follow",response)
            }).catch(error=>{
                console.log(error.response);
            })
        }
        else if(obj.follow==true){
            axios.post("http://127.0.0.1:8000/minitwitter/users/"+id+"/followings/",{"following_id":obj.id})
            .then(response=>{
                console.log("Follow",response)
            }).catch(error=>{
                console.log(error.response);
                this.setState({
                    error_message:error.response.data
                })
            })
        }

        }
        componentDidMount(){
            axios.get("http://127.0.0.1:8000/minitwitter/user/logged-in/").then(response=>{
            console.log("logged id side",response)
             const storeid=response.data["id"]
            //  this.props.takeID(storeid)
            // // localStorage.setItem("id",storeid) 
          this.setState({
            id:storeid
           })


        }).catch(error=>{
            console.log(error.response)
        })
    }
    render() {
     // const{id}=this.state
     const{Searchdata,search,error_message}=this.state
        return (
            <div>
            <div>

                <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/minitwitter/Timeline/")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followersbutton" onClick={()=>{history.push("/minitwitter/Followers/")}}><i class="fa fa-user-friends"></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton" onClick={()=>{history.push("/minitwitter/Followings/")}}><i class="fa fa-user-plus" ></i>Following</button>
            <br/>
            <br/>
    <button className="change profilebutton" onClick={()=>{history.push("/minitwitter/UserProfile/")}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>history.push("/minitwitter/Tweets/")}>Tweet</button>


            <button type="button"  className="buttonstyle" onClick={this.removeitem}  >LogOut</button>
            </div>  


            </div>
            <div>

            <i class="fa fa-search" area-hidden="true"></i> 
      <input value={search} onChange={this.changeserach} type="text" className="searchbox" placeholder="Search Mini-twitter"></input>

      <button className="Searchbtn" onClick={this.search} type="submit" >Search</button>
      <br/><br/><br/><br/>
<div  className="search_div">{Searchdata.length? Searchdata.map(Search=><div key={Search.id}>
<div className="timelineuserstyle2"><i class="fa fa-user-circle"></i> </div>
<div className="timelinetext1">{Search.first_name}{Search.last_name}</div>
<div className="search_username">@{Search.username} <div> <button onClick={()=>this.followusers(Search.follow,Search.id)} className="textchange">
{Search.follow==true?"Following":"Follow"}</button></div>{error_message}<hr className="searchHr"></hr> 

      </div></div>):"No Search"}</div>

     </div>
     </div>
        )

    }
}

export default SideBar
