import React, { Component } from 'react'
import axios from 'axios'

export class follow extends Component {

    constructor(props) {
        super(props)

        this.state = {
             query:"",
            token:localStorage.getItem("token"),
            storeuserdata:[],
            error_message:""
        }
    }

followfun=()=>{
  axios.post("http://127.0.0.1:8000/follow/",this.state).then(response=>{
      console.log(response);
      if(response.status="200"){
          this.setState({storeuserdata:response.data})
      }
  }).catch(error=>{
      console.log(error.response);
      this.setState({error_message:error.response.data["message"]})

  })
}
searchapi=()=>{
    axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
        console.log(response);


    }).catch(error=>{
        console.log(error.response);
    })
    console.log(this.state.token);
}
  changeserach=(event)=>{
      this.setState({query:event.target.value})
  }

     render() {

         const{query,storeuserdata,error_message}=this.setState
        return (
            <div>
            <div>

 <div>@</div><button type="button"className="followersbutton" onClick={this.followfun}>Follow</button></div>
            <input type="text" value={query} onChange={this.changeserach}></input>
           <div><div className="messagestyling">{error_message}</div></div> 
            <button onClick={this.searchapi}>Search</button>
             <div>{}</div>
           </div>
        )
    }
}

export default follow
