import React, { Component } from 'react'
import history from '../history'
import TwitterLogo from  "./images/twitter.png"
import axios from "axios"


 class SideBar extends Component {
     constructor(props) {
         super(props)

         this.state = {
             id:"" 
         }

     }


        componentDidMount(){
            axios.defaults.headers={
                Authorization:"token "+localStorage.getItem("token")
            }
            axios.get("http://127.0.0.1:8000/minitwitter/user/logged-in/").then(response=>{
                console.log("logged id",response)
                 const storeID=response.data["id"]
                 this.setState({
                     id:storeID
                 })

                 console.log(this.state.id)

            }).catch(error=>{
                console.log(error.response)
            })
        }




    removeitem(){
        localStorage.removeItem("token");
        localStorage.removeItem("id");
        {history.push("/Login")}
    }

    render() {
      const{id}=this.state
        return (
            <div>

                <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followersbutton" onClick={()=>{history.push("/Followers")}}><i class="fa fa-user-friends"></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton" onClick={()=>{history.push("/Followings")}}><i class="fa fa-user-plus" ></i>Following</button>
            <br/>
            <br/>
    <button className="change profilebutton" onClick={()=>{history.push("/UserProfile/"+id)}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets")}>Tweet</button>
            <button type="button"  className="buttonstyle" onClick={this.removeitem}  >LogOut</button>
            </div>  


            </div>
        )
    }
}

export default SideBar
