import React, { Component } from 'react'
import './homestyle.css'
import Logo from './images/logo.jpg'
import axios from 'axios'
import history from '../history'


export class UserProfile extends Component {

    constructor(props) {
        super(props)

        this.state = {
             first_name:"",
             last_name:"",
             username:"",
             error_Message:"",
             user_bio:"",   
             token:localStorage.getItem("token"),

        }



    }


    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })
    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    changeUserBio=(event)=>{
        this.setState({
            user_bio:event.target.value
        })
    }
    componentDidMount(){
        axios
        .post("http://127.0.0.1:8000/profile/",this.state)
        .then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({first_name:response.data["first_name"],
                           last_name:response.data["last_name"],
                           username:response.data["username"],
                           user_bio:response.data["user_bio"]

           })
        }


    }).catch(error=>{
        if(error.response.status="400"){
            this.setState({error_Message:error.response.data["message"]});
            console.log(error.response);
        }

    })


    }
    tweetshow=()=>{
        axios.post(" http://127.0.0.1:8000/tweets/",this.state).then(response=>{
      console.log(response.data);
        })
    }

    // Show=()=>{
    //     if(){
    //         document.getElementById("anchortag").style.visibility="visible";
    //     }

    // }


    removeitem(){
        localStorage.removeItem("token");
    }



    render() {
        const {first_name,last_name,username,error_Message,user_bio}=this.state
        return (
       <div className="Home">
           <div className="showprofilediv">
            <center> <img src={Logo} /></center> 
            <h1>User Profile</h1>
                <div className="lableStyle"onChange={this.changeFirstName}><label>First Name:<text className="t">{first_name}</text></label></div> <br></br>
             <div className="lableStyle" onChange={this.changeLastName}><label>Last Name: <text className="t">{last_name}</text></label></div> <br></br>
                 <div className="lableStyle" onChange={this.changeUserName} ><label>Username: <text className="t">{username}</text></label></div> <br></br>
                 <div className="lableStyle" onChange={this.changeUserBio} ><label>UserBio: <text className="t">{user_bio}</text></label></div> <br></br>
                 <button type="button"  className="buttonstyle" onClick={this.removeitem}>LogOut</button>
                <div><strong>{error_Message}</strong></div>
               <br></br>
               <br></br>
               {/* <a id="anchortag" style={{visibility:"hidden"}} onClick={()=>{history.push("/Login")}}>Login</a> */}
            <br></br>
             </div>
          </div>

        )
    }
}

export default UserProfile
