import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import "./timeline.css"

export class timeline extends Component {
    constructor(props) {
        super(props)
        this.state = {
             token:localStorage.getItem("token"),
            // token2:localStorage.getItem("token"),
             showfollow:[],
             content:[],
             Error:"",
             error_message:""
        }
    }
    componentDidMount(){
        axios.post("http://127.0.0.1:8000/tweets/timeline/",this.state).then(response=>{
            console.log(response);
            if(response.status="200"){
                this.setState({content:response.data})
            }
            if(response.status="204"){
                //this.setState({error_message:response.statusText})
            }

        }).catch(error=>{
            if(error.response.status="401"){
                this.setState({Error:error.response.data["message"]})
            }
            // if(error.response.status="204"){
            //     this.setState({error_message:error.response.stausText})
            // }
        })
    }
    DislayFollowers=()=>{
        axios.post("http://127.0.0.1:8000/followers/",this.state).then(response=>{
            console.log(response);
        }).catch(error=>{
            console.log(error.response);
        })
    //   this.setState({
    //       username
    //   })
    }
    DisplayFollowing=()=>{
        axios.post("http://127.0.0.1:8000/following/",this.state).then(response=>{
            console.log(response);
        }).catch(error=>{
            console.log(error.response);
        })
    }
    // showtimeline=()=>{
    //     axios.post("http://127.0.0.1:8000/tweets/timeline/",this.state).then(response=>{
    //         console.log(response);
    //     }).catch(error=>{
    //         console.log(error.response);
    //     })
    // }
    render() {
        const {content,Error,error_message}=this.state
        return (
            <div>
                <div className="leftpart">
            <button className="change homebutton"><i class="fa fa-home"></i>Home</button>
            <br></br>
            <br></br>
            <button className="change followersbutton"><i class="fa fa-user-friends"></i>Followers</button>
            <br></br>
            <br></br>
            <button className="change followingbutton"><i class="fa fa-user-plus"></i>Following</button>
            <br></br>
            <br></br>
            <button className="change profilebutton"><i class="fa fa-user"></i>Profile</button>
            <br></br>
            <br></br>
            <button className="tweetbutton">Tweet</button>
            </div> 
            <div className="rightpart">
                      <h2 className="styleOfh2">InstaTweets</h2>
                      <div className="styleofimg"><img src={Small_logo}></img></div>
                 <div style={{height:"0px",width:"102%",borderBottom:"2px solid black",marginTop:"4px",marginLeft:"-1%"}}></div><br></br>
                <h2 style={{fontWeight:"bold",textShadow:"1px 1px 1px black"}}>Home</h2>
               <hr class="hrstyle"></hr>
        <div className="timeline">{content.length? content.map(con=> <div><div key={con.id} className="timelinetext1">{con.content}<div className="bottomline"></div></div></div>):null}</div>
        {Error}
        <div className="nocontent">{error_message}</div>
                </div> 
                {/* <button type="button" onClick={this.showtimeline}>Show TimeLine</button> */}
                {/* <button type="button"onClick={this.DislayFollowers}>Display Followers</button>
                <button type="button"onClick={this.DisplayFollowing}>Display Following</button> */}
            </div>
        )
    }
}

export default timeline
