import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import history from '../history'
import "./showfollowings.css"
import TwitterLogo from  "./images/twitter.png"
import SideBar from './SideBar'
import UniversalSearch from './UniversalSearch'
export class DisplayFollowings extends Component {
    constructor(props) {
        super(props)

        this.state = {
            UserInfo:[],
            following:[],
            id:localStorage.getItem("id")



        }
    }

    componentDidMount(){
    const{id}=this.state
    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }

       axios.get("http://127.0.0.1:8000/minitwitter/users/"+id+"/followings/").then(response=>{
           if(response.status="200"){
            //    this.setState({
            //     })

           }
            console.log(response)
       }).catch(error=>{
           console.log(error.response);
       })

       const storeid=localStorage.getItem("id")
       axios.get("http://127.0.0.1:8000/minitwitter/users/"+storeid+"/profile/").then(response=>{
           if(response.status="200"){
               this.setState({
                   UserInfo:response.data["user"],
            })

           }

           console.log(response)
       }).catch(error=>{
           console.log(error.response);
       })


    }


   unfollow=(unfollouser)=>{
    const obj={
        userid:unfollouser
    }
    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }
    const storeid=localStorage.getItem("id")
    axios.delete("http://127.0.0.1:8000/user/"+storeid+"/unfollow/"+obj.userid).then(response=>{
        console.log(response)
    }).catch(error=>{
        console.log(error.response);
    })

      // event.preventDefault();
    }

   showprofile=(userid)=>{
       history.push("/User/"+userid);

   }


    render() {
        const{UserInfo,following}=this.state
        return (
            <div>

            <div className="rightpart">

        <div className="homestyle">{UserInfo.first_name} {UserInfo.last_name}</div>
        <div className="DF_username">{UserInfo.username}</div>

        <button className="DF_followingsbtn" >Followings</button>
        <hr className="DE_hr"></hr>


        { following.length ? following.map(follow=><div key={follow.following__id} className="Tofocus"> 
        <div className="timelineuserstyle"> 
        <i class="fa fa-user-circle"></i></div>
        <a className="anchor" onClick={()=>this.showprofile(follow.following__id)}>

            <div className="DF_firstName">{follow.following__first_name} {follow.following__last_name}</div></a>
        <div className="DF_user_name">@{follow.following__username}</div>
        <button className="btnchange"  
        onClick={()=>this.unfollow(follow.id)} >Unfollow</button>
       <div className="bottomline"></div></div>):<center><h2>Zero Followings</h2></center>}



           </div>


            </div>


        )
    }
}

export default DisplayFollowings
