import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import './tweet.css'
import history from '../history'

export class Tweets extends Component {
    constructor(props) {
        super(props)
        this.state = {
            token:localStorage.getItem("token"),
            content:"",
            tweets:[],
            username:"",
            message:"",
            message2:""



        }
    }
    componentDidMount(){
        axios.post("http://127.0.0.1:8000/profile/",this.state).then(response=>{
            if(response.status="200"){
                this.setState({username:response.data["username"]})
            }
        })
    }
    usernamechange=(event)=>{
        this.setState({username:event.target.value})
    }
    tweetcreate=()=>{
        axios.post("http://127.0.0.1:8000/tweets/create/",this.state).then(response=>{
            console.log(response);
        }).catch(e=>{
            if(e.response.status="400"){
                this.setState({message:e.response.data["message"]})
            }
              console.log(e.response);
        })
    }
    showtweets=(event)=>{
        this.setState({
            content:event.target.value
        })

    }


    displaytweets=()=>{
        axios.post(" http://127.0.0.1:8000/tweets/",this.state).then(response=>{
      console.log(response);
      if(response.status="200"){
          this.setState({tweets:response.data});
      }
      if(response.status="204"){
          this.setState({message2:response.statusText})
      }

        }).catch(error=>{
            if(error.response.status="400")
            this.setState({message:error.response.data["message"]})
                console.log(error.response);
        })

    }
    render() {
        const {content,tweets,message,username,message2}=this.state
        return (
            <div>
            <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <br></br>
            <br></br>
            <button className="change followersbutton"><i class="fa fa-user-friends"></i>Followers</button>
            <br></br>
            <br></br>
            <button className="change followingbutton"><i class="fa fa-user-plus"></i>Following</button>
            <br></br>
            <br></br>
            <button className="change profilebutton" onClick={()=>{history.push("/UserProfile")}}><i class="fa fa-user"></i>Profile</button>
            <br></br>
            <br></br>
            <button className="tweetbutton">Tweet</button>
            </div>  
            <div className="rightpart2">
            <h2 className="styleOfh2">InstaTweets</h2>
            <div className="styleofimg"><img src={Small_logo}></img></div>
           <div style={{height:"0px",width:"102%",borderBottom:"2px solid black",marginTop:"4px",marginLeft:"-1%"}}></div>
           <h2 style={{fontWeight:"bold",textShadow:"1px 1px 1px black"}}>Tweets Section</h2>
           <hr class="hrstyle"></hr>
           <div>
            <input  className="styleofinput" type="text" value={content} onChange={this.showtweets} placeholder="Enter a tweet"></input><br></br>
            <div className="messagestyling">{message}</div>
             <br></br>
             <br></br>

            <button className="tweetbutton2" type="submit" onClick={this.tweetcreate}  >Tweet</button>

            <button className="displaybutton" type="button" onClick={this.displaytweets}>Display Tweets</button>
        <div className="outerdiv">{tweets.length? tweets.map(tweet=> <div key={tweet.id} id="size" className="tweetstext1"><div className="usernamestyle">@{username}</div>{tweet.content}<div id="size2" className="tweetstext2"> {tweet.created_date} </div><hr class="hrstyle1"></hr></div>):null}</div>
            </div>
        <div className="messagestyling">{message2}</div>
      </div>    

            </div>
        )
    }
}

export default Tweets
