import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import history from '../history'
import "./showfollowings.css"
import TwitterLogo from  "./images/twitter.png"
export class DisplayFollowings extends Component {
    constructor(props) {
        super(props)

        this.state = {
             token:localStorage.getItem("token"),
             first_name:"",
             last_name:"",
             username:"",
             error_message:"",
             followings:[],
             error_message2:"",
             userdata:[],
             query:""
        }
    }
    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })

    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    componentDidMount(){
        axios
        .post("http://127.0.0.1:8000/profile/",this.state)
        .then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({first_name:response.data["first_name"],
                           last_name:response.data["last_name"],
                           username:response.data["username"],
           })

        }


    }).catch(error=>{
       if(error.response.status="400"){
            this.setState({error_Message:error.response.data["message"]});
            console.log(error.response);
        }

    })

        axios.post("http://127.0.0.1:8000/following/",this.state).then(resposnse=>{
            console.log(resposnse)
            if(resposnse.status="200"){

               this.setState({followings:resposnse.data.followings})


            }

        }).catch(error=>{
            // if(error.resposnse.status="400"){
            //     this.setState({error_message:error.resposnse.data["message"]})
            // }
            // if(error.resposnse.status="401"){
            //     this.setState({error_message:error.resposnse.data["message"]})
            // }
            console.log(error.resposnse)

        })

    }
    changeserach=(event)=>{
        this.setState({query:event.target.value})
    }
    searchapi=()=>{
        axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
            console.log(response);

            if(response.status="200"){

                const userarray=response.data;
                console.log(userarray);

                this.setState({userdata:userarray})

                console.log(this.state.userdata);



            }


        }).catch(error=>{
            console.log(error.response);
             this.setState({error_message:error.response.data["message"]})
        })
        console.log(this.state.token);
    }
   unfollow=(unfollouser,event)=>{
    const obj={
        token:this.state.token,
        username:unfollouser
    }
       axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
           console.log(response.data)
       }).catch(error=>{

           console.log(error.response)
       })

      // event.preventDefault();
   }
   followusers=(followfield,nameuser)=>{
    const obj={
        follow:followfield,
        username:nameuser,
        token:this.state.token
    }
    if(obj.follow==1){
        axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
            console.log(response.data)
        }).catch(error=>{

            console.log(error.response)
        })
       document.getElementById("textchange").innerHTML="f"
    }
    else{
        axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
            console.log(response);
        }).catch(error=>{
            console.log(error.response);
        })
        document.getElementById("textchange").innerHTML="t" 
    }
}
   hoverchange(){
      var tagbtn= document.getElementsByClassName("btnchange");
      for(var i=0;i<tagbtn.length;i++){
          tagbtn[i].innerHTML="Unfollow";
          tagbtn[i].style.backgroundColor="#DF6240";

      }
    //    document.getElementsByTagName("button").style.backgroundColor="#DF6240 ";
       return false;
   }
   hoverout(){

      var tagbtn1= document.getElementsByClassName("btnchange");
      for(var i=0;i<tagbtn1.length;i++){
        tagbtn1[i].innerHTML="Following";
         tagbtn1[i].style.backgroundColor="#4ea7f0";
      }
       //document.getElementsByTagName("button").style.backgroundColor="#4ea7f0 ";
       return false;
   }
    render() {
        const{error_message,followings,first_name,last_name,username,userdata,query}=this.state
        return (
            <div>
                <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followersbutton"onClick={()=>{history.push("/Followers")}} ><i class="fa fa-user-friends"></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton" onClick={()=>{history.push("/Followings")}}><i class="fa fa-user-plus"></i>Following</button>
            <br/>
            <br/>
            <button className="change profilebutton" onClick={()=>{history.push("/UserProfile")}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets")}>Tweet</button>
            </div>  
            <div className="rightpart">

        <div className="homestyle">{first_name} {last_name}</div>
        <div className="DF_username">{username}</div>

        <button className="DF_followingsbtn" >Followings</button>
        <hr className="DE_hr"></hr>

            {/* {followings.map(follow=><div key={follow.id} className="styleoffirst_name">{follow.first_name}<div className="styleoflast_name">{follow.last_name} */}
            {/* <div className="styleofusername">@{follow.username}</div> */}
            {/* <button className="unfollowstyle" onClick={()=>{this.unfollow(follow.username)}}>Unfollow</button></div><hr className="styleof_hr"></hr></div></div>)} */}

        { followings.length ? followings.map(follow=><div key={follow.id} className="Tofocus"> <div className="timelineuserstyle"> <i class="fa fa-user-circle"></i></div><div className="DF_firstName">{follow.first_name} {follow.last_name}</div>
        <div className="DF_user_name">@{follow.username}</div>
        <button className="btnchange"  onMouseOver={this.hoverchange} onMouseOut={this.hoverout} 
        onClick={()=>this.unfollow(follow.username)} >Following</button><div className="bottomline"></div></div>): <h2>Zero Followings</h2>}

<div className="timeline">{userdata.map (user=><div key={user.id}><div className="timelineuserstyle"><i class="fa fa-user-circle"></i> </div><div className="timelinetext1">{user.first_name} {user.last_name}</div>
        <div className="search_username">@{user.username}
        <button onClick={()=>this.followusers(user.follow,user.username)} id="textchange">Follow</button>
       </div><div className="bottomline"></div></div>)}</div> 

           </div>
           <i class="fa fa-search" area-hidden="true"></i>  
          <input type="text" value={query} onChange={this.changeserach} className="searchbox" placeholder="Search Mini-twitter" ></input>

          <button className="Searchbtn" onClick={this.searchapi}>Search</button>

            {error_message}

            </div>


        )
    }
}

export default DisplayFollowings
