import React, { Component } from 'react'
import history from '../history'
import TwitterLogo from  "./images/twitter.png"
import axios from "axios"
import Routes from '../Routes'


 class SideBar extends Component {
     constructor(props) {
         super(props)

         this.state = {
             id:""
         }
         console.log("SIDE",this.props.id)

     }

    removeitem(){
        localStorage.removeItem("token");
        localStorage.removeItem("id");
        {history.push("/Login")}
    }

    render() {
      const{id}=this.state
        return (
            <div>

                <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline/"+localStorage.getItem("id"))}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followersbutton" onClick={()=>{history.push("/Followers/"+localStorage.getItem("id"))}}><i class="fa fa-user-friends"></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton" onClick={()=>{history.push("/Followings/"+localStorage.getItem("id"))}}><i class="fa fa-user-plus" ></i>Following</button>
            <br/>
            <br/>
    <button className="change profilebutton" onClick={()=>{history.push("/UserProfile/"+localStorage.getItem("id"))}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets/"+localStorage.getItem("id"))}>Tweet</button>
            <button type="button"  className="buttonstyle" onClick={this.removeitem}  >LogOut</button>
            </div>  
            </div>
        )

    }
}

export default SideBar
