import React, { Component } from 'react'
import './Userprofile.css'
// import Logo from './images/logo.jpg'
import axios from 'axios'
//import history from '../history'
import Usericon from "./images/User_icon.jpg"
import Small_logo from "./images/Small_logo.jpg"
import "./Mainpage.css"
import history from '../history'

export class UserProfile extends Component {

    constructor(props) {
        super(props)

        this.state = {
             first_name:"",
             last_name:"",
             username:"",
             error_Message:"",
             user_bio:"",   
             following_count:"",
             followers_count:"",
             token:localStorage.getItem("token"),
             tweets:[],


        }



    }


    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })
    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    changeUserBio=(event)=>{
        this.setState({
            user_bio:event.target.value
        })
    }
    changeFollow=(event)=>{
        this.setState({
            following_count:event.target.value
        })
    }
    changeFollowers=(event)=>{
        this.setState({
            followers_count:event.target.value
        })
    }
    componentDidMount(){

        axios
        .post("http://127.0.0.1:8000/profile/",this.state)
        .then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({first_name:response.data["first_name"],
                           last_name:response.data["last_name"],
                           username:response.data["username"],
                           user_bio:response.data["user_bio"],
                           following_count:response.data["following_count"],
                           followers_count:response.data["followers_count"]


           })

        }


    }).catch(error=>{
       if(error.response.status="400"){
            this.setState({error_Message:error.response.data["message"]});
            console.log(error.response);
        }

    })
        if(this.state.token!=null){
            console.log(this.state.token);
        }
        else{
            console.log("not present");
        }




    }


    removeitem(){
        localStorage.removeItem("token");
    }



    render() {
        const {first_name,last_name,username,error_Message,user_bio,following_count,followers_count,tweets}=this.state
        return (
            <div>
            <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <br></br>
            <br></br>
            <button className="change followersbutton"><i class="fa fa-user-friends"onClick={()=>{history.push("/Followers")}}></i>Followers</button>
            <br></br>
            <br></br>
            <button className="change followingbutton"><i class="fa fa-user-plus" onClick={()=>{history.push("/Following")}}></i>Following</button>
            <br></br>
            <br></br>
            <button className="change profilebutton"><i class="fa fa-user"onClick={()=>{history.push("/UserProfile")}}></i>Profile</button>
            <br></br>
            <br></br>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets")}>Tweet</button>
            </div>  
            <div className="rightpart">
              <h2 className="styleOfh2">InstaTweets</h2>
              <div className="styleofimg"><img src={Small_logo}></img></div>
             <div style={{height:"0px",width:"102%",borderBottom:"2px solid black",marginTop:"4px",marginLeft:"-1%"}}></div>


           <div className="showprofilediv">
           <h3 >User Profile</h3>
           <center><img src={Usericon} className="userstyle"></img></center>
        <div className="lableStyle"onChange={this.changeFirstName} onChange={this.changeLastName}><text className="firstName_lastName">{first_name} {last_name}</text></div> <br></br>
              <div className="lableStyle" onChange={this.changeUserName} ><text className="">@{username}</text></div> <br></br>
                 <div className="lableStyle" onChange={this.changeUserBio} ><label>UserBio: <text className="t">{user_bio}</text></label></div> <br></br>
                 <div className="lableStyle" onChange={this.changeFollowers} ><text className="t">{followers_count}</text><label>Followers</label></div> <br></br>
                 <div className="lableStyle" onChange={this.changeFollow} > <text className="t">{following_count}</text><label>Following</label></div><br></br>
                 <center><button type="button" className="biobutton" onClick={()=>history.push("/Edit")} >Edit Bio</button><br></br></center>
                 <button type="button"  className="buttonstyle" onClick={this.removeitem}>LogOut</button>

               {/* <a id="anchortag" style={{visibility:"hidden"}} onClick={()=>{history.push("/Login")}}>Login</a> */}

             </div>
                <div><strong>{error_Message}</strong></div>

             </div>  
             </div>
        )
    }
}

export default UserProfile
