import React, { Component } from 'react'
import './homestyle.css'
import Logo from './images/logo.jpg'
import axios from 'axios'
import history from '../history'


export class UserProfile extends Component {

    constructor(props) {
        super(props)

        this.state = {
             first_name:"",
             last_name:"",
             username:"",
             error_Message:"",
             token:localStorage.getItem("token"),
             content:""
        }



    }


    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })
    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    componentDidMount(){
        axios
        .post("http://127.0.0.1:8000/profile/",this.state)
        .then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({first_name:response.data["first_name"],
                           last_name:response.data["last_name"],
                           username:response.data["username"]

           })
        }


    }).catch(error=>{
        if(error.response.status="400"){
            this.setState({error_Message:error.response.data["message"]});
            console.log(error.response);
        }

    })


    }
    tweetchange=()=>{
        axios.post("http://127.0.0.1:8000/tweets/create/",this.state).then(response=>{
            console.log(response);
        }).catch(e=>{
              console.log(e.response);
        })
    }
    showtweets=(event)=>{
        this.setState({
            content:event.target.value
        })

    }



    removeitem(){
        localStorage.removeItem("token");
    }



    render() {
        const {first_name,last_name,username,error_Message,content}=this.state
        return (
       <div className="Home">
           <div className="showprofilediv">
            <center> <img src={Logo} /></center> 
            <h1>User Profile</h1>
                <div className="lableStyle"onChange={this.changeFirstName}><label>First Name:</label></div> <br></br>
             <div className="lableStyle" onChange={this.changeLastName}><label>Last Name:</label><text className="t">{last_name}</text></div> <br></br>
                 <div className="lableStyle" onChange={this.changeUserName} ><label>Username:</label><text className="t">{username}</text></div> <br></br>
                 <button type="button" className="buttonstyle" onClick={this.removeitem}>LogOut</button>
               <div>{error_Message}</div>
               <br></br>
               <br></br>
               <input type="text" value={content} onChange={this.showtweets}></input>
             <br></br>
            <button type="submit" onClick={this.tweetchange}>Tweet</button>
             </div>

          </div>

        )
    }
}

export default UserProfile
