import React, { Component } from 'react'
import './homestyle.css'
import Logo from './images/logo.jpg'
import axios from 'axios'
import history from '../history'


export class UserProfile extends Component {

    constructor(props) {
        super(props)

        this.state = {
             first_name:"",
             last_name:"",
             username:"",
             token:localStorage.getItem("token")
        }


        //this.displaychange=this.displaychange.bind(this);
    }

    changeFirstName=(event)=>{
        this.setState({
            first_name:first_name
        })
    }

    changeLastName=(event)=>{
        this.setState({
            last_name:last_name
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:username
        })
    }
    displaychange(){
        console.log(this.state);
      console.log("data");

             axios.post("http://127.0.0.1:8000/profile/",this.state).then(response=>{
             console.log(response);
             if(response.status="200"){

                     const first_name=response.data["first_name"]
                    const last_name=response.data["last_name"]
                    const username=response.data["username"]

             }
         }).catch(error=>{
            console.log(error.response);
         })

        } 


    removeitem(){
        localStorage.removeItem("token");
    }


    // axios.post("http://127.0.0.1:8000/register/",this.state).then(response=>{
    //     console.log(response);

    render() {
        {this.displaychange()};
        return (
         <div className="Home">
                 {/* {this.displaychange()}  */}
             <div className="showprofilediv">
              <center> <img src={Logo} /></center>
              <h1>User Profile</h1>
                 <div className="lableStyle"onChange={this.changeFirstName}><label>First Name:</label><text>{this.state.first_name}</text></div> <br></br>
              <div className="lableStyle" onChange={this.changeLastName}><label>Last Name:</label><text>{this.state.last_name}</text></div> <br></br>
                   <div className="lableStyle" onChange={this.changeUserName} ><label>Username:</label><text>{this.state.username}</text></div> <br></br>
                   <button type="button" className="buttonstyle" onClick={this.removeitem}>LogOut</button>
              </div>
            </div>



        )
    }
}

export default UserProfile
