import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import history from '../history'
export class follow extends Component {

    constructor(props) {
        super(props)

        this.state = {
             query:"",
            token:localStorage.getItem("token"),
            userdata:[],
            error_message:""
        }
    }

// followfun=()=>{
