searchapi=()=>{
    axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
        console.log(response);

        if(response.status="200"){
            const userarray=response.data[0];
            this.setState({userdata:userarray})
        //    const userdata=response.data;
        //    console.log(userdata);

        }


    }).catch(error=>{
        console.log(error.response);
        this.setState({error_message:error.response.data["message"]})
    })
    console.log(this.state.token);
}
  changeserach=(event)=>{
      this.setState({query:event.target.value})
  }

     render() {

         const{query,userdata,error_message}=this.setState
        return (
            <div>
    <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <br></br>
            <br></br>
            <button className="change followersbutton"><i class="fa fa-user-friends" onClick={()=>{history.push("/Followers")}}></i>Followers</button>
            <br></br>
            <br></br>
            <button className="change followingbutton"><i class="fa fa-user-plus" onClick={()=>{history.push("/Followings")}}></i>Following</button>
            <br></br>
            <br></br>
            <button className="change profilebutton"><i class="fa fa-user" onClick={()=>{history.push("/UserProfile")}}></i>Profile</button>
            <br></br>
            <br></br>
            <button className="tweetbutton" onClick={()=>{history.push("/Tweets")}}>Tweet</button>
            </div>  
            <div className="rightpart">
              <h2 className="styleOfh2">InstaTweets</h2>
              <div className="styleofimg"><img src={Small_logo}></img></div>
             <div style={{height:"0px",width:"102%",borderBottom:"2px solid black",marginTop:"4px",marginLeft:"-1%"}}></div>
 {/* <div>@</div><button type="button"className="followersbutton" onClick={this.followfun}>Follow</button></div> */}
            <input type="text" value={query} onChange={this.changeserach} className="firstname"></input>
           <div><div className="messagestyling">{error_message}</div></div> 
           <br></br>
           <br></br>
            <button onClick={this.searchapi} className="savebutton">Search</button>
            <div>{userdata}</div>
        {/* <div>{userdata.map(users=> <div key={users.indexdb}>{users.username}</div>)}</div> */}
           </div>
           </div>
        )
    }
}

export default follow
