import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import history from '../history'
import "./showfollowings.css"
import TwitterLogo from  "./images/twitter.png"
export class DisplayFollowings extends Component {
    constructor(props) {
        super(props)

        this.state = {
             token:localStorage.getItem("token"),
             error_message:"",
             followings:[],
             error_message2:""
        }
    }
    componentDidMount(){
        axios.post("http://127.0.0.1:8000/following/",this.state).then(resposnse=>{
            console.log(resposnse)
            if(resposnse.status="200"){
               this.setState({followings:resposnse.data.followings})
            }
        }).catch(error=>{
            if(error.resposnse.status="400"){
                this.setState({error_message:error.resposnse.data["message"]})
            }
            if(error.resposnse.status="401"){
                this.setState({error_message:error.resposnse.data["message"]})
            }
            console.log(error.resposnse)
            console.log(error.resposnse);
        })
    }
   unfollow=(unfollouser,event)=>{
    const obj={
        token:this.state.token,
        username:unfollouser
    }
       axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
           console.log(response.data)
       }).catch(error=>{

           console.log(error.response)
       })

   }
    render() {
        const{error_message,followings}=this.state
        return (
            <div>
                <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followeusersbutton"  onClick={()=>{history.push("/Follow")}}><i class="fa fa-list"></i>Follow Users</button>
            <br/>
            <br/>
            <button className="change followersbutton"><i class="fa fa-user-friends"></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton"><i class="fa fa-user-plus"></i>Following</button>
            <br/>
            <br/>
            <button className="change profilebutton"><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets")}>Tweet</button>
            </div>  
            <div className="rightpart">

              <div className="styleofimg"><img src={Small_logo}></img></div>
             <div style={{height:"0px",width:"102%",borderBottom:"2px solid black",marginTop:"4px",marginLeft:"-1%"}}></div>

            {followings.map(follow=><div key={follow.id} className="styleoffirst_name">{follow.first_name}<div className="styleoflast_name">{follow.last_name}
            <div className="styleofusername">@{follow.username}</div><div>
            <button className="unfollowstyle" onClick={()=>{this.unfollow(follow.username)}}>Unfollow</button></div><hr className="styleof_hr"></hr></div></div>)}

            {error_message}

            </div>

            </div>
        )
    }
}

export default DisplayFollowings
