import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import "./edit.css"
import history from '../history'
import TwitterLogo from  "./images/twitter.png"

class Edit extends Component {
    constructor(props) {
        super(props)

        this.state = {
             first_name:"",
             last_name:"",
             bio:"",
             user_bio:"",
             username:"",
             token:localStorage.getItem("token"),
             savemessage:"",
             error_message:"",
             userdata:[],
             query:""
        }
    }
    componentDidMount(){
        axios
        .post("http://127.0.0.1:8000/profile/",this.state)
        .then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({first_name:response.data["first_name"],
                           last_name:response.data["last_name"],
                           username:response.data["username"],
                           bio:response.data["user_bio"],


           })

        }


    }).catch(error=>{
       if(error.response.status="400"){
            this.setState({error_Message:error.response.data["message"]});
            console.log(error.response);
        }

    })
    }
    changeserach=(event)=>{
        this.setState({query:event.target.value})
    }

    followusers=(followfield,nameuser)=>{
        const obj={
            follow:followfield,
            username:nameuser,
            token:this.state.token
        }
        if(obj.follow==1){
            axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
                console.log(response.data)
            }).catch(error=>{

                console.log(error.response)
            })

        }
        else{
            axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
                console.log(response);
            }).catch(error=>{
                console.log(error.response);
            })

        }
    }
    savedata=()=>{
        const storeid=localStorage.getItem("id")
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token") 
        }
     axios.patch("http://127.0.0.1:8000/user/profile/",{"bio":this.state.bio}).then(response=>{
         console.log(response);
         this.setState({savemessage:response.data["message"]})

     }).catch(error=>{
         console.log(error.response);
         this.setState({error_message:error.response.data["message"]})
     })
     console.log(this.state.token);
    }
    firstnamechange=(event)=>{
        this.setState({first_name:event.target.value})
    }
    lastnamechange=(event)=>{
        this.setState({last_name:event.target.value})
    }
    usernamechange=(event)=>{
        this.setState({username:event.target.value})
    }
    userbiochange=(event)=>{
        this.setState({bio:event.target.value})
    }
    removeitem(){
        localStorage.removeItem("token");
        {history.push("/Login")}
    }
    render() {
        const{first_name,last_name,bio,username,savemessage,error_message,query,userdata,}=this.state
        return (
            <div>
            <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div> 
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followersbutton" onClick={()=>{history.push("/Followers")}}><i class="fa fa-user-friends" ></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton" onClick={()=>{history.push("/Followings")}}><i class="fa fa-user-plus" ></i>Following</button>
            <br/>
            <br/>
            <button className="change profilebutton" onClick={()=>{history.push("/UserProfile/"+localStorage.getItem("username"))}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>{history.push("/Tweets")}}>Tweet</button>
            <button type="button"  className="buttonstyle" onClick={this.removeitem} >LogOut</button>
            </div> 
            <div className="rightpart">
            <div className="homestyle">{first_name} {last_name}</div>
         <div className="DF_username">{username}</div>
         <div className="BottomPart"> </div>

            <div>
            <div className="editheading">Edit Profile </div>
            <button type="submit" onClick={this.savedata} className="savebutton" >Save</button>
            <div className="BottomPart"> </div>
              <form className="editform">
                 <div className="labelstyle1"><label>First Name</label></div>
              <div className="inputstyle1">  <input type="text"  value={first_name} onChange={this.firstnamechange} placeholder="Change FirstName" required ></input></div>
                <br/>
                <br/>
                <div className="labelstyle1"><label>Last Name</label></div>
              <div className="inputstyle1"> <input type="text"  value={last_name} onChange={this.lastnamechange} placeholder="Change LastName" required></input></div> 
                <br/>
                <br/>
                <div className="labelstyle1"><label>Bio</label></div>
               <div className="inputstyle1"> <input type="text"  value={bio} onChange={this.userbiochange} placeholder="Change UserBio" required></input></div>
                <br/>
                <br/>

             <div className="messagestyling2">{savemessage}</div>
             <div className="messagestyling">{error_message}</div>
                </form>
            </div>
            </div>
            <i class="fa fa-search" area-hidden="true"></i>  
          <input type="text" value={query} onChange={this.changeserach} className="searchbox" placeholder="Search Mini-twitter" ></input>

          <button className="Searchbtn" onClick={this.searchapi}>Search</button>
          <br/><br/><br/><br/>
          <div className="search_div">{userdata.length ? userdata.map (user=><div key={user.id}><div className="timelineuserstyle2"><i class="fa fa-user-circle"></i> </div><div className="timelinetext1">{user.first_name} {user.last_name}</div>
        <div className="search_username">@{user.username}
       <div className="changebtn"> <button onClick={()=>this.followusers(user.follow,user.username)} className="textchange">Follow</button></div>
      <hr className="searchHr"></hr></div></div>):<div>NO Search</div>} </div>
            </div>
        )
    }
}

export default Edit
