import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import history from '../history'
import "./showfollowings.css"
import TwitterLogo from  "./images/twitter.png"
import SideBar from './SideBar'
import UniversalSearch from "./UniversalSearch"

class DisplayFollowings extends Component {
    constructor(props) {
        super(props)

        this.state = {
            UserInfo:[],
            following:[],
            // id:localStorage.getItem("id"),
            id:this.props.match.props



        }
    }

    componentDidMount(){
    const{id}=this.state
    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }

    axios.get("http://127.0.0.1:8000/minitwitter/users/"+id+"/profile/").then(response=>{
           if(response.status="200"){
               this.setState({
                   UserInfo:response.data["user"],
            })

           }

           console.log(response)
       }).catch(error=>{
           console.log(error.response);
       })
       axios.get("http://127.0.0.1:8000/minitwitter/users/"+id+"/followings/").then(response=>{
           if(response.status="200"){
               this.setState({
                   following:response.data
                })

           }
            console.log("Followings",response)
       }).catch(error=>{
           console.log(error.response);
       })


    }


   unfollow=(unfollouser)=>{
       const {id}=this.state
    const obj={
        userid:unfollouser
    }
    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }
    axios.delete("http://127.0.0.1:8000/minitwitter/users/"+id+"/followings/"+obj.userid).then(response=>{
        console.log(response)
    }).catch(error=>{
        console.log(error.response);
    })

       history.push("/Timeline")
    }

   showprofile=(userid)=>{
    history.push("/UserProfile/"+userid);

   }


    render() {
        const{UserInfo,following}=this.state
        return (
            <div>
                 <SideBar></SideBar>
            <div className="rightpart">

        <div className="homestyle">{UserInfo.first_name} {UserInfo.last_name}</div>
        <div className="DF_username">{UserInfo.username}</div>

        <button className="DF_followingsbtn" >Followings</button>
        <hr className="DE_hr"></hr>


        { following.length ? following.map(follow=><div key={follow.following.id} className="Tofocus"> 
        <div className="timelineuserstyle"> 
        <i class="fa fa-user-circle"></i></div>
        <a className="anchor" onClick={()=>this.showprofile(follow.following.id)}>

            <div className="DF_firstName">{follow.following.first_name} {follow.following.last_name}</div></a>
        <div className="DF_user_name">@{follow.following.username}</div>
        <button className="btnchange"  
        onClick={()=>this.unfollow(follow.id)} >Unfollow</button>
       <div className="bottomline"></div></div>):<center><h2>Zero Followings</h2></center>}



           </div>
           <UniversalSearch></UniversalSearch>

            </div>


        )
    }
}

export default DisplayFollowings
