import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import history from '../history'
import "./showfollowings.css"
import TwitterLogo from  "./images/twitter.png"
import SideBar from './SideBar'
export class followers extends Component {
    constructor(props) {
        super(props)

        this.state = {
             token:localStorage.getItem("token"),
             followers:[],
             followErr:"",
             username:"",
             first_name:"",
             last_name:"",
             userdata:[],
             query:""

        }
    }

    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })

    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    changeserach=(event)=>{
        this.setState({query:event.target.value})
    }

    componentDidMount(){
        axios
        .post("http://127.0.0.1:8000/profile/",this.state)
        .then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({first_name:response.data["first_name"],
                           last_name:response.data["last_name"],
                           username:response.data["username"],
           })

        }


    }).catch(error=>{
       if(error.response.status="400"){
            this.setState({error_Message:error.response.data["message"]});
            console.log(error.response);
        }

    })
        axios.post("http://127.0.0.1:8000/followers/",this.state).then(resposnse=>{
            console.log(resposnse)
            if(resposnse.status="200"){
                this.setState({followers:resposnse.data.followers})
            }
        }).catch(error=>{
            console.log(error.resposnse);
        })
    }
    // Follow=(followuser)=>{
    //     const obj={
    //         token:this.state.token,
    //         username:followuser,

    //     }
    //     console.log(obj.username);
    //         axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
    //             console.log(response);
    //             if(response.status="200"){
    //                 this.setState({followErr:response.data["message"]})
    //             }
    //         }).catch(error=>{
    //             console.log(error.response);
    //         })
    // }
    searchapi=()=>{
        axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
            console.log(response);

            if(response.status="200"){

                const userarray=response.data;
                console.log(userarray);

                this.setState({userdata:userarray})

                console.log(this.state.userdata);



            }


        }).catch(error=>{
            console.log(error.response);
             this.setState({error_message:error.response.data["message"]})
        })
        console.log(this.state.token);
    }

    showprofile(username){

        {history.push(("/UserProfile/"+username));
     }
    }

    followusers=(followfield,nameuser)=>{
        const obj={
            follow:followfield,
            username:nameuser,
            token:this.state.token
        }
        if(obj.follow==1){
            axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
                console.log(response.data)
            }).catch(error=>{

                console.log(error.response)
            })

        }
        else{
            axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
                console.log(response);
            }).catch(error=>{
                console.log(error.response);
            })

        }
    }
    removeitem(){
        localStorage.removeItem("token");
        {history.push("/Login")}
    }
    render() {
        const{followers,followErr,username,last_name,first_name,userdata,query}=this.state
        return (
            <div>
              <SideBar></SideBar>
            <div className="rightpart">

            <div className="homestyle">{first_name} {last_name}</div>
        <div className="DF_username">{username}</div>
        <button className="DF_followingsbtn" >Followers</button>
        <hr className="DE_hr"></hr>

        {/* {followers.length? followers.map(Followers=> <div key={Followers.id} className="styleoffirst_name">{Followers.first_name}
        <div className="styleoflast_name">{Followers.last_name}
        <div className="styleofusername">@{Followers.username}<div>
            <button onClick={()=>this.Follow(Followers.username)}>Follow</button></div></div><hr className="styleof_hr"></hr></div></div>):null} */}
             {followers.length ? followers.map(follower=><div key={follower.id} className="Tofocus"> <div className="timelineuserstyle"> <i class="fa fa-user-circle"></i></div><div className="DF_firstName">
        <a className="anchor" onClick={()=>this.showprofile(follower.username)}>{follower.first_name} {follower.last_name}</a></div>
        <div className="DF_user_name">@{follower.username}</div><div className="bottomline"></div></div>):<center><h2>Zero Followers</h2></center>}
            {/* <div>{followErr}</div>  */}
            </div>
            <i class="fa fa-search" area-hidden="true"></i>  
          <input type="text" value={query} onChange={this.changeserach} className="searchbox" placeholder="Search Mini-twitter" ></input>

          <button className="Searchbtn" onClick={this.searchapi}>Search</button>
          <br/><br/><br/><br/>
          <div className="search_div">{userdata.map (user=><div key={user.id}><div className="timelineuserstyle"><i class="fa fa-user-circle"></i> </div><div className="timelinetext1">{user.first_name} {user.last_name}</div>
        <div className="search_username">@{user.username}
       <div> <button onClick={()=>this.followusers(user.follow,user.username)} 
       className="textchange">{user.follow==1?"Unfollow":"Follow"}</button></div><hr className="searchHr"></hr>
       </div></div>)}</div> 
            </div>
        )
    }
}

export default followers
