import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import './tweet.css'
import history from '../history'
import TwitterLogo from  "./images/twitter.png"
import SideBar from './SideBar'

export class Tweets extends Component {
    constructor(props) {
        super(props)
        this.state = {
            token:localStorage.getItem("token"),
            content:"",
            tweets:[],
            username:"",
            message:"",
            message2:"",
            first_name:"",
            last_name:"",
            userdata:[],
             UserInfo:""



        }
    }

    usernamechange=(event)=>{
        this.setState({username:event.target.value})
    }
    tweetcreate=()=>{
        axios.post("http://127.0.0.1:8000/tweets/create/",this.state).then(response=>{
            console.log(response);
        }).catch(e=>{
            if(e.response.status="400"){
                this.setState({message:e.response.data["message"]})
            }
              console.log(e.response);
        })
    }
    Chan=(event)=>{
        this.setState({
            content:event.target.value
        })

    }


    componentDidMount(){


    //     const username=this.props.match.params.username
    //     axios
    //     .post("http://127.0.0.1:8000/profile/",{"username":username,"token":this.state.token})
    //     .then(response=>{
    //     console.log(response);
    //     if(response.status="200"){
    //         this.setState({first_name:response.data["first_name"],
    //                        last_name:response.data["last_name"],
    //                        username:response.data["username"],
    //        })

    //     }


    // }).catch(error=>{
    //     console.log(error.response);
    // })



    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }
    const storeid=localStorage.getItem("id")
       axios.get("http://127.0.0.1:8000/user/"+storeid+"/profile/").then(response=>{
           if(response.status="200"){
               this.setState({
                   UserInfo:response.data["user"],
            })

           }

           console.log(response)
       }).catch(error=>{
           console.log(error.response);
       })

      axios.defaults.headers={
          Authorization:"token "+localStorage.getItem("token")
      }

        axios.post("http://127.0.0.1:8000/tweets/"+storeid).then(response=>{
      console.log(response);
      if(response.status="200"){
          this.setState({tweets:response.data});
      }
    //   if(response.status="204"){
    //       this.setState({message2:"zero tweets found"})
    //   }

        }).catch(error=>{
            if(error.response.status="204"){
                this.setState({message2:"Zero Tweets Found"})
            }
            if(error.response.status="400")
            this.setState({message:error.response.data["message"]})
                console.log(error.response);
        })

    }
    searchapi=()=>{
        axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
            console.log(response);

            if(response.status="200"){

                const userarray=response.data;
                console.log(userarray);

                this.setState({userdata:userarray})

                console.log(this.state.userdata);



            }


        }).catch(error=>{
            console.log(error.response);
             this.setState({error_message:error.response.data["message"]})
        })
        console.log(this.state.token);
    }

    changeserach=(event)=>{
        this.setState({query:event.target.value})
    }
    followusers=(followfield,nameuser)=>{
        const obj={
            follow:followfield,
            username:nameuser,
            token:this.state.token
        }
        if(obj.follow==1){
            axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
                console.log(response.data)
            }).catch(error=>{

                console.log(error.response)
            })
            var SelectClass= document.getElementsByClassName("textchange")
          for(var i=0;i<SelectClass.length;i++){
            SelectClass[i].innerHTML="Unfollow"
          }
        }
        else{
            axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
                console.log(response);
            }).catch(error=>{
                console.log(error.response);
            })
            var SelectClass2= document.getElementsByClassName("textchange")
          for(var i=0;i<SelectClass2.length;i++){
            SelectClass2[i].innerHTML="Follow"
          }
        }
    }
    removeitem(){
        localStorage.removeItem("token");
        {history.push("/Login")}
    }
    render() {
        const {content,tweets,message,username,message2,first_name,last_name,userdata,query,UserInfo}=this.state
        return (
            <div>
            <SideBar></SideBar>
            <div className="rightpart">
            <div className="homestyle">{UserInfo.first_name} {UserInfo.last_name}</div>
         <div className="DF_username">{UserInfo.username}</div>
         <div className="BottomPart"> </div>


            {/* <input  className="styleofinput" type="text" value={content} onChange={this.showtweets}  placeholder={username}></input><br/>
            <div className="messagestyling">{message}</div> */}
             <div><i class="fa fa-user-circle"></i> </div> 
               <textarea value={content} onChange={this.showtweets} className="tweettextarea" placeholder="What's happening"></textarea> 
              <br/>
            <button className="tweet_tweetbtn" type="submit" onClick={this.tweetcreate}  >Tweet</button>
            <div className="error_message">{message} </div> 
            <br/>
            <br/>
           <div className="BottomPart2">  </div>
        {/* <div className="outerdiv">
        {tweets.length? tweets.map(tweet=> <div key={tweet.id} id="size" className="tweetstext1">
            <div className="usernamestyle">{first_name}{last_name}@{username}
            </div>{tweet.content}<div id="size2" className="tweetstext2"> 
            {tweet.created_date} </div><hr class="hrstyle1"></hr></div>):null}</div> */}
        <div className="timeline">
        {tweets.length? tweets.map(tweet=><div key={tweet.id} className="Tofocus" >
        <div className="timelineuserstyle"><i class="fa fa-user-circle"></i> </div><div className="timelinetext1">{first_name} {last_name}  @{username}</div> 
       <div className="contentstyle">{tweet.content}</div><div className="bottomline"></div></div>):null}</div>
    <div className="messagestyling">{message2}</div>

      </div>    
      <i class="fa fa-search" area-hidden="true"></i>  
          <input type="text" value={query} onChange={this.changeserach} className="searchbox" placeholder="Search Mini-twitter" ></input>

          <button className="Searchbtn" onClick={this.searchapi}>Search</button>
          <br/><br/><br/><br/>
          <div className="search_div">{userdata.map (user=><div key={user.id}><div className="timelineuserstyle"><i class="fa fa-user-circle"></i> </div><div className="timelinetext1">{user.first_name} {user.last_name}</div>
        <div className="search_username">@{user.username}
        <div><button onClick={()=>this.followusers(user.follow,user.username)} className="textchange">{user.follow==1?"Unfollow":"Follow"}</button></div><hr className="searchHr"></hr>
       </div></div>)}</div> 

            </div>
        )
    }
}

export default Tweets
