import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"

export class timeline extends Component {
    constructor(props) {
        super(props)
        this.state = {
             token:localStorage.getItem("token"),
            // token2:localStorage.getItem("token"),
             showfollow:[],
             Content:[],
             Error:[]
        }
    }
    componentDidMount(){
        axios.post("http://127.0.0.1:8000/tweets/timeline/",this.state).then(response=>{
            if(response.status="200"){
                this.setState({Content:response.data["content"]})
            }

        }).catch(error=>{
            if(error.response.status="400"){

            }
        })
    }
    DislayFollowers=()=>{
        axios.post("http://127.0.0.1:8000/followers/",this.state).then(response=>{
            console.log(response);
        }).catch(error=>{
            console.log(error.response);
        })
    //   this.setState({
    //       username
    //   })
    }
    DisplayFollowing=()=>{
        axios.post("http://127.0.0.1:8000/following/",this.state).then(response=>{
            console.log(response);
        }).catch(error=>{
            console.log(error.response);
        })
    }
    showtimeline=()=>{
        axios.post("http://127.0.0.1:8000/tweets/timeline/",this.state).then(response=>{
            console.log(response);
        }).catch(error=>{
            console.log(error.response);
        })
    }
    render() {
        return (
            <div>
                <div className="leftpart">
            <button className="change homebutton"><i class="fa fa-home"></i>Home</button>
            <br></br>
            <br></br>
            <button className="change followersbutton"><i class="fa fa-user-friends"></i>Followers</button>
            <br></br>
            <br></br>
            <button className="change followingbutton"><i class="fa fa-user-plus"></i>Following</button>
            <br></br>
            <br></br>
            <button className="change profilebutton"><i class="fa fa-user"></i>Profile</button>
            <br></br>
            <br></br>
            <button className="tweetbutton">Tweet</button>
            </div> 
            <div className="rightpart">
                      <h2 className="styleOfh2">InstaTweets</h2>
                      <div className="styleofimg"><img src={Small_logo}></img></div>
                 <div style={{height:"0px",width:"102%",borderBottom:"2px solid black",marginTop:"4px",marginLeft:"-1%"}}></div>

                </div> 
                <button type="button" onClick={this.showtimeline}>Show TimeLine</button>
                {/* <button type="button"onClick={this.DislayFollowers}>Display Followers</button>
                <button type="button"onClick={this.DisplayFollowing}>Display Following</button> */}
            </div>
        )
    }
}

export default timeline
