import React, { Component } from 'react'
import './Userprofile.css'
// import Logo from './images/logo.jpg'
import axios from 'axios'
//import history from '../history'
import Usericon from "./images/User_icon.jpg"
import Usericon2 from "./images/User_icon2.jpg"
import Small_logo from "./images/Small_logo.jpg"
import "./Mainpage.css"
import history from '../history'
import TwitterLogo from  "./images/twitter.png"

class UserProfile extends Component {

    constructor(props) {
        super(props)

        this.state = {
             error_Message:"",
             bio:"",   
             token:localStorage.getItem("token"),
             UserInfo:"",
             followers:[],
             following:[],
             q:""


        }

        console.log(props);

    }


    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })
    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    changeUserBio=(event)=>{
        this.setState({
            bio:event.target.value
        })
    }


    componentDidMount(){

    //     const username=this.props.match.params.username
    //     axios
    //     .post("http://127.0.0.1:8000/profile/",{"username":username,"token":this.state.token})
    //     .then(response=>{
    //     console.log(response);
    //     if(response.status="200"){
    //         this.setState({first_name:response.data["first_name"],
    //                        last_name:response.data["last_name"],
    //                        username:response.data["username"],
    //                        user_bio:response.data["user_bio"],
    //                        following_count:response.data["following_count"],
    //                        followers_count:response.data["followers_count"]


    //        })

    //     }


    // }).catch(error=>{
    //    if(error.response.status="400"){
    //         this.setState({error_Message:error.response.data["message"]});
    //         console.log(error.response);
    //     }

    // })

    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }
       axios.get("http://127.0.0.1:8000/user/profile/").then(response=>{
           if(response.status="200"){
               this.setState({
                   UserInfo:response.data,
                   bio:response.data["bio"],
                   followers:response.data["followers"],
                   following:response.data["following"]

                })

           }

           console.log(response)
       }).catch(error=>{
           console.log(error.response);
       })


    }
    followusers=(followfield,nameuser)=>{
        const obj={
            follow:followfield,
            username:nameuser,
            token:this.state.token
        }
        if(obj.follow==1){
            axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
                console.log(response.data)
            }).catch(error=>{

                console.log(error.response)
            })

        }
        else{
            axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
                console.log(response);
            }).catch(error=>{
                console.log(error.response);
            })

        }
    }
    changeserach=(event)=>{
        this.setState({q:event.target.value})
    }


    search=()=>{
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token") 
        }
        axios.get("http://127.0.0.1:8000/users/search/",{params:{q:this.state.q}}).then(response=>{
            console.log(response)
        }).catch(error=>{
            console.log(error.response)
        })
    }

    removeitem(){
        localStorage.removeItem("token");
        {history.push("/Login")}
    }



    render() {
        const {UserInfo,bio,followers,following,q}=this.state
        return (
            <div>

            <div className="rightpart">

        <div className="homestyle">{UserInfo.full_name}</div> 
       {/* <div className="noOftweets">{tweets.length} Tweets</div>  */}

            <div className="BottomPart"> </div>
             <div className="graydiv"></div>
             <div class="circle"><img src={Usericon} className="userstyle"></img></div>

           <div className="showprofilediv">

        <div onChange={this.changeFirstName} onChange={this.changeLastName} className="styleofFL" >
            <text className="firstName_lastName" >{UserInfo.full_name}</text>

            </div> 
            <button type="button" className="editbutton" onClick={()=>history.push("/Edit")} >Edit Profile</button><br/>
        <div onChange={this.changeUserName} className="styleofFL" >
            <text className="user">@{UserInfo.username}</text>
            </div> 

            <div  onChange={this.changeUserBio} >
                <text className="userbio">{bio}</text></div> 
                <br/><br/>

            <div>
            <text className="followersCount">{followers.length} Followers</text>
            </div>

           <div > <text className="followingCount" > {following.length} Following</text></div>
           <br/><br/><br/>
           <button className="tweetbtnofprofile" >Tweets</button>
           <hr className="profilehr"></hr>


             </div>
             </div>
             <input value={q} onChange={this.changeserach} type="text"></input>
             <button onClick={this.search} >Search</button>
             </div>
        )
    }
}

export default UserProfile
