import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import './tweet.css'
import history from '../history'
import TwitterLogo from  "./images/twitter.png"
import SideBar from './SideBar'
import UniversalSearch from './UniversalSearch'

class Tweets extends Component {
    constructor(props) {
        super(props)
        this.state = {
            content:"",
            tweets:"",

            message:"",

            first_name:"",
            last_name:"",
            userdata:[],
             UserInfo:"",
            UsersTweets:[]



        }
    }

    usernamechange=(event)=>{
        this.setState({username:event.target.value})
    }
    tweetcreate=()=>{
        axios.defaults.headers={
            Authorization:"token "+localStorage.getItem("token")
        }

        const storeid=localStorage.getItem("id")
          axios.post("http://127.0.0.1:8000/tweets/"+storeid,{"content":this.state.content}).then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({tweets:response.data})

        }

          }).catch(error=>{
              if(error.response.status="400")
              this.setState({message:error.response.data["content"]})
                  console.log(error.response);
          })
    }
    Changetweetinput=(event)=>{
        this.setState({
            content:event.target.value
        })

    }


    componentDidMount(){


    //     const username=this.props.match.params.username
    //     axios
    //     .post("http://127.0.0.1:8000/profile/",{"username":username,"token":this.state.token})
    //     .then(response=>{
    //     console.log(response);
    //     if(response.status="200"){
    //         this.setState({first_name:response.data["first_name"],
    //                        last_name:response.data["last_name"],
    //                        username:response.data["username"],
    //        })

    //     }


    // }).catch(error=>{
    //     console.log(error.response);
    // })



    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }
    const storeid=localStorage.getItem("id")
       axios.get("http://127.0.0.1:8000/user//profile/").then(response=>{
           if(response.status="200"){
               this.setState({
                   UserInfo:response.data["user"],
            })

           }

           console.log(response)
       }).catch(error=>{
           console.log(error.response);
       })

       axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token")
    }

      axios.get("http://127.0.0.1:8000/user/"+storeid+"/tweets/").then(response=>{
    console.log(response);
    if(response.status="200"){
       this.setState({UsersTweets:response.data})

    }

      }).catch(error=>{
          if(error.response.status="400")
        //  this.setState({message:error.response.data["content"]})
              console.log(error.response);
      })


    }
    searchapi=()=>{
        axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
            console.log(response);

            if(response.status="200"){

                const userarray=response.data;
                console.log(userarray);

                this.setState({userdata:userarray})

                console.log(this.state.userdata);



            }


        }).catch(error=>{
            console.log(error.response);
             this.setState({error_message:error.response.data["message"]})
        })
        console.log(this.state.token);
    }

    changeserach=(event)=>{
        this.setState({query:event.target.value})
    }
    followusers=(followfield,nameuser)=>{
        const obj={
            follow:followfield,
            username:nameuser,
            token:this.state.token
        }
        if(obj.follow==1){
            axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
                console.log(response.data)
            }).catch(error=>{

                console.log(error.response)
            })

        }
        else{
            axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
                console.log(response);
            }).catch(error=>{
                console.log(error.response);
            })

        }
    }
    removeitem(){
        localStorage.removeItem("token");
        {history.push("/Login")}
    }
    render() {
        const {content,message,userdata,query,UserInfo,UsersTweets}=this.state
        return (
            <div>
            <SideBar></SideBar>
            <div className="rightpart">
            <div className="homestyle">{UserInfo.first_name} {UserInfo.last_name}</div>
         <div className="DF_username">{UserInfo.username}</div>
         <div className="BottomPart"> </div>


             <div><i class="fa fa-user-circle"></i> </div> 
               <textarea value={content} onChange={this.Changetweetinput} className="tweettextarea" placeholder="What's happening"></textarea> 
              <br/>
            <button className="tweet_tweetbtn" type="submit" onClick={this.tweetcreate}  >Tweet</button>
            <div className="error_message">{message} </div> 
            <br/>
            <br/>
           <div className="BottomPart2">  </div>

            <div className="timeline">
        {UsersTweets.length? UsersTweets.map(tweet=><div key={tweet.id} className="Tofocus" >
        <div className="timelineuserstyle">
            <i class="fa fa-user-circle"></i> 
            </div><div className="timelinetext1">{tweet.first_name} {tweet.last_name}  @{tweet.username}</div> 
       <div className="contentstyle">{tweet.content}</div><div className="bottomline"></div></div>):"Zero tweets"}</div>


      </div>    
     <UniversalSearch></UniversalSearch>
     </div>
        )
    }
}

export default Tweets
