import React, { Component } from 'react'
import './Userprofile.css'
// import Logo from './images/logo.jpg'
import axios from 'axios'
//import history from '../history'
import Usericon from "./images/User_icon.jpg"
import Usericon2 from "./images/User_icon2.jpg"
import Small_logo from "./images/Small_logo.jpg"
import "./Mainpage.css"
import history from '../history'
import TwitterLogo from  "./images/twitter.png"

class UserProfile extends Component {

    constructor(props) {
        super(props)

        this.state = {
             error_Message:"",
             bio:"",   
             following_count:"",
             followers_count:"",
             token:localStorage.getItem("token"),
             tweets:[],
             UserInfo:"",
             followers:[],
             following:[],
             query:""


        }

        console.log(props);

    }


    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })
    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    changeUserBio=(event)=>{
        this.setState({
            bio:event.target.value
        })
    }
    changeFollow=(event)=>{
        this.setState({
            following_count:event.target.value
        })
    }
    changeFollowers=(event)=>{
        this.setState({
            followers_count:event.target.value
        })
    }


    componentDidMount(){

    //     const username=this.props.match.params.username
    //     axios
    //     .post("http://127.0.0.1:8000/profile/",{"username":username,"token":this.state.token})
    //     .then(response=>{
    //     console.log(response);
    //     if(response.status="200"){
    //         this.setState({first_name:response.data["first_name"],
    //                        last_name:response.data["last_name"],
    //                        username:response.data["username"],
    //                        user_bio:response.data["user_bio"],
    //                        following_count:response.data["following_count"],
    //                        followers_count:response.data["followers_count"]


    //        })

    //     }


    // }).catch(error=>{
    //    if(error.response.status="400"){
    //         this.setState({error_Message:error.response.data["message"]});
    //         console.log(error.response);
    //     }

    // })

    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }
       axios.get("http://127.0.0.1:8000/user/profile/").then(response=>{
           if(response.status="200"){
               this.setState({
                   UserInfo:response.data["user"],
                   bio:response.data["bio"],
                   followers:response.data["followers"],
                   following:response.data["following"]

                })

           }
           console.log(response)
       }).catch(error=>{
           console.log(error.response);
       })


    }
    followusers=(followfield,nameuser)=>{
        const obj={
            follow:followfield,
            username:nameuser,
            token:this.state.token
        }
        if(obj.follow==1){
            axios.post("http://127.0.0.1:8000/unfollow/",obj).then(response=>{
                console.log(response.data)
            }).catch(error=>{

                console.log(error.response)
            })

        }
        else{
            axios.post("http://127.0.0.1:8000/follow/",obj).then(response=>{
                console.log(response);
            }).catch(error=>{
                console.log(error.response);
            })

        }
    }
    changeserach=(event)=>{
        this.setState({query:event.target.value})
    }
    search=()=>{
        axios.post("http://127.0.0.1:8000/search/",this.state).then(response=>{
            console.log(response);

            if(response.status="200"){

                const userarray=response.data;
                console.log(userarray);

                this.setState({userdata:userarray})

                console.log(this.state.userdata);



            }


        }).catch(error=>{
            console.log(error.response);
             this.setState({error_message:error.response.data["message"]})
        })
        console.log(this.state.token);
    }

    removeitem(){
        localStorage.removeItem("token");
        {history.push("/Login")}
    }



    render() {
        const {UserInfo,bio,followers,following,query}=this.state
        return (
            <div>
            <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div>  
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followersbutton" onClick={()=>{history.push("/Followers")}}><i class="fa fa-user-friends"></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton" onClick={()=>{history.push("/Followings")}}><i class="fa fa-user-plus" ></i>Following</button>
            <br/>
            <br/>
            <button className="change profilebutton"onClick={()=>{history.push("/UserProfile/"+localStorage.getItem("username"))}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>history.push("/Tweets")}>Tweet</button>
            <button type="button"  className="buttonstyle" onClick={this.removeitem}  >LogOut</button>
            </div>  
            <div className="rightpart">

        <div className="homestyle">{UserInfo.first_name} {UserInfo.last_name}</div> 
       {/* <div className="noOftweets">{tweets.length} Tweets</div>  */}

            <div className="BottomPart"> </div>
             <div className="graydiv"></div>
             <div class="circle"><img src={Usericon} className="userstyle"></img></div>

           <div className="showprofilediv">

        <div onChange={this.changeFirstName} onChange={this.changeLastName} className="styleofFL" >
            <text className="firstName_lastName" >{UserInfo.first_name} {UserInfo.last_name}</text>

            </div> 
            <button type="button" className="editbutton" onClick={()=>history.push("/Edit")} >Edit Profile</button><br/>
        <div onChange={this.changeUserName} className="styleofFL" >
            <text className="user">@{UserInfo.username}</text>
            </div> 

            <div  onChange={this.changeUserBio} >
                <text className="userbio">{bio}</text></div> 
                <br/><br/>

            <div>
            <text className="followersCount">{followers.length} Followers</text>
            </div>

           <div > <text className="followingCount" > {following.length} Following</text></div>
           <br/><br/><br/>
           <button className="tweetbtnofprofile" >Tweets</button>
           <hr className="profilehr"></hr>


             </div>
             </div>
             <input value={query} onChange={this.changeserach} type="text"></input>
             <button onClick={this.search} >Search</button>
             </div>
        )
    }
}

export default UserProfile
