import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import "./edit.css"
import history from '../history'
import TwitterLogo from  "./images/twitter.png"

export class Edit extends Component {
    constructor(props) {
        super(props)

        this.state = {
             first_name:"",
             last_name:"",
             bio:"",
             user_bio:"",
             username:"",
             token:localStorage.getItem("token"),
             savemessage:"",
             error_message:""
        }
    }
    componentDidMount(){
        axios
        .post("http://127.0.0.1:8000/profile/",this.state)
        .then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({first_name:response.data["first_name"],
                           last_name:response.data["last_name"],
                           username:response.data["username"],
                           bio:response.data["user_bio"],


           })

        }


    }).catch(error=>{
       if(error.response.status="400"){
            this.setState({error_Message:error.response.data["message"]});
            console.log(error.response);
        }

    })
    }
    savedata=()=>{
     axios.post("http://127.0.0.1:8000/profile/edit/",this.state).then(response=>{
         console.log(response);
         this.setState({savemessage:response.data["message"]})

     }).catch(error=>{
         console.log(error.response);
         this.setState({error_message:error.response.data["message"]})
     })
     console.log(this.state.token);
    }
    firstnamechange=(event)=>{
        this.setState({first_name:event.target.value})
    }
    lastnamechange=(event)=>{
        this.setState({last_name:event.target.value})
    }
    usernamechange=(event)=>{
        this.setState({username:event.target.value})
    }
    userbiochange=(event)=>{
        this.setState({bio:event.target.value})
    }
    render() {
        const{first_name,last_name,bio,username,savemessage,error_message}=this.state
        return (
            <div>
            <div className="leftpart">
            <button className="change homebutton" onClick={()=>{history.push("/Timeline")}}><i class="fa fa-home"></i>Home</button>
            <div className="twitter_logo"><img src={TwitterLogo} ></img></div> 
            <br/>
            <br/>
            <br/>
            <br/>
            <button className="change followersbutton"><i class="fa fa-user-friends"onClick={()=>{history.push("/Followers")}} ></i>Followers</button>
            <br/>
            <br/>
            <button className="change followingbutton"><i class="fa fa-user-plus" onClick={()=>{history.push("/Followings")}}></i>Following</button>
            <br/>
            <br/>
            <button className="change profilebutton" onClick={()=>{history.push("/UserProfile")}}><i class="fa fa-user"></i>Profile</button>
            <br/>
            <br/>
            <button className="tweetbutton" onClick={()=>{history.push("/Tweets")}}>Tweet</button>
            </div> 
            <div className="rightpart">
            <div className="homestyle">{first_name} {last_name}</div>
         <div className="DF_username">{username}</div>
         <div className="BottomPart"> </div>

            <div>
            <div className="editheading">Edit Profile</div>
              <form className="editform">
                 <div className="labelstyle1"><label>First Name</label></div>
              <div className="inputstyle1">  <input type="text"  value={first_name} onChange={this.firstnamechange} placeholder="Change FirstName" required ></input></div>
                <br></br>
                <br></br>
                <div className="labelstyle1"><label>First Name</label></div>
              <div></div>  <input type="text" className="lastname" value={last_name} onChange={this.lastnamechange} placeholder="Change LastName" required></input>
                <br></br>
                <br></br>
                <input type="text" className="bioname" value={bio} onChange={this.userbiochange} placeholder="Change UserBio" required></input>
                <br></br>
                <br></br>
                <center><button type="submit" onClick={this.savedata} className="savebutton" >Save</button></center>
                     <div className="messagestyling2">{savemessage}</div>
                    <div className="messagestyling">{error_message}</div>
                </form>
            </div>
            </div>
            </div>
        )
    }
}

export default Edit
