import React, { Component } from 'react'
import axios from 'axios'

export class Edit extends Component {
    constructor(props) {
        super(props)

        this.state = {
             first_name:"",
             last_name:"",
             bio:"",
             token:localStorage.getItem("token")
        }
    }
    savedata=()=>{
     axios.post("http://127.0.0.1:8000/profile/edit/",this.state).then(response=>{
         console.log(response);
     }).catch(error=>{
         console.log(error.response);
     })
     console.log(this.state.token);
    }
    firstnamechange=(event)=>{
        this.setState({first_name:event.target.value})
    }
    lastnamechange=(event)=>{
        this.setState({last_name:event.target.value})
    }
    userbiochange=(event)=>{
        this.setState({bio:event.target.value})
    }
    render() {
        const{first_name,last_name,bio}=this.state
        return (
            <div>
            <div className="leftpart">
            <button className="change homebutton"><i class="fa fa-home"></i>Home</button>
            <br></br>
            <br></br>
            <button className="change followersbutton"><i class="fa fa-user-friends"></i>Followers</button>
            <br></br>
            <br></br>
            <button className="change followingbutton"><i class="fa fa-user-plus"></i>Following</button>
            <br></br>
            <br></br>
            <button className="change profilebutton"><i class="fa fa-user"></i>Profile</button>
            <br></br>
            <br></br>
            <button className="tweetbutton">Tweet</button>
            </div> 

            <div>
                <input type="text" value={first_name} onChange={this.firstnamechange} ></input>
                <br></br>
                <input type="text"  value={last_name} onChange={this.lastnamechange}></input>
                <br></br>
                <input type="text" value={bio} onChange={this.userbiochange}></input>
                <button type="submit" onClick={this.savedata}>Save</button>
            </div>
            </div>
        )
    }
}

export default Edit
