import React, { Component } from 'react'
import axios from 'axios'
import Small_logo from "./images/Small_logo.jpg"
import history from '../history'
import "./showfollowings.css"
import TwitterLogo from  "./images/twitter.png"
import SideBar from './SideBar'
export class followers extends Component {
    constructor(props) {
        super(props)

        this.state = {
            UserInfo:[],
             error_message:"",

             followers:[],
             error_message2:"",



        }
    }
    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })

    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    componentDidMount(){
    //     const username=this.props.match.params.username
    //     axios
    //     .post("http://127.0.0.1:8000/profile/",{"username":username,"token":this.state.token})
    //     .then(response=>{
    //     console.log(response);
    //     if(response.status="200"){
    //         this.setState({first_name:response.data["first_name"],
    //                        last_name:response.data["last_name"],
    //                        username:response.data["username"],
    //        })

    //     }


    // }).catch(error=>{
    //     console.log(error.response);
    // })


    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }

       axios.get("http://127.0.0.1:8000/user/profile/").then(response=>{
           if(response.status="200"){
               this.setState({
                   UserInfo:response.data["user"],
                   bio:response.data["bio"],
                   followers:response.data.followers,
                   following:response.data.following

                })

           }

           console.log(response)
       }).catch(error=>{
           console.log(error.response);
       })



    }


   unfollow=(unfollouser)=>{
    const obj={
        userid:unfollouser
    }
    axios.defaults.headers={
        Authorization:"token "+localStorage.getItem("token") 
    }
    const storeid=localStorage.getItem("id")
    axios.delete("http://127.0.0.1:8000/user/"+storeid+"/unfollow/"+obj.userid).then(response=>{
        console.log(response)
    }).catch(error=>{
        console.log(error.response);
    })

      // event.preventDefault();
   }

    showprofile(username){

        {history.push(("/UserProfile/"+username));
     }
    }

   removeitem(){
    localStorage.removeItem("token");
    {history.push("/Login")}
}
    render() {
        const{UserInfo,followers}=this.state
        return (
            <div>
               <SideBar></SideBar>
            <div className="rightpart">

        <div className="homestyle">{UserInfo.first_name} {UserInfo.last_name}</div>
        <div className="DF_username">{UserInfo.username}</div>

        <button className="DF_followingsbtn" >Followings</button>
        <hr className="DE_hr"></hr>


        { followers.length ? followers.map(follow=><div key={follow.following__id} className="Tofocus"> 
        <div className="timelineuserstyle"> 
        <i class="fa fa-user-circle"></i></div>
        <a className="anchor" onClick={()=>this.showprofile(follow.username)}>

            <div className="DF_firstName">{follow.following__first_name} {follow.following__last_name}</div></a>
        <div className="DF_user_name">@{follow.following__username}</div>
        <button className="btnchange"  
        onClick={()=>this.unfollow(follow.id)} >Unfollow</button>
       <div className="bottomline"></div></div>):<center><h2>Zero Followings</h2></center>}



           </div>


            </div>


        )
    }
}

export default followers
