import React, { Component } from 'react'
import axios from 'axios'

export class timeline extends Component {
    constructor(props) {
        super(props)
        this.state = {
             token:localStorage.getItem("token"),
            // token2:localStorage.getItem("token"),
             showfollow:[]
        }
    }
    DislayFollowers=()=>{
        axios.post("http://127.0.0.1:8000/followers/",this.state).then(response=>{
            console.log(response);
        }).catch(error=>{
            console.log(error.response);
        })
    //   this.setState({
    //       username
    //   })
    }
    DisplayFollowing=()=>{
        axios.post("http://127.0.0.1:8000/following/",this.state).then(response=>{
            console.log(response);
        }).catch(error=>{})
    }
    showtimeline=()=>{
        axios.post("http://127.0.0.1:8000/tweets/timeline/",this.state).then(response=>{
            console.log(response);
        }).catch(error=>{
            console.log(error.response);
        })
    }
    render() {
        return (
            <div>
                <button type="button" onClick={this.showtimeline}>Show TimeLine</button>
                <button type="button"onClick={this.DislayFollowers}>Display Followers</button>
            </div>
        )
    }
}

export default timeline
