import React, { Component } from 'react'
import './homestyle.css'
import Logo from './images/logo.jpg'
import axios from 'axios'
import history from '../history'
import Tweets from './Tweets'
import Usericon from "./images/User_icon.jpg"
import Small_logo from "./images/Small_logo.jpg"
import "./Mainpage.css"

export class UserProfile extends Component {

    constructor(props) {
        super(props)

        this.state = {
             first_name:"",
             last_name:"",
             username:"",
             error_Message:"",
             user_bio:"",   
             following_count:"",
             followers_count:"",
             token:localStorage.getItem("token"),
             tweets:[]

        }



    }


    changeFirstName=(event)=>{
        this.setState({
            first_name:event.target.value
        })
    }

    changeLastName=(event)=>{
        this.setState({
            last_name:event.target.value
        })
    }
    changeUserName=(event)=>{
        this.setState({
            username:event.target.value
        })
    }
    changeUserBio=(event)=>{
        this.setState({
            user_bio:event.target.value
        })
    }
    changeFollow=(event)=>{
        this.setState({
            following_count:event.target.value
        })
    }
    changeFollowers=(event)=>{
        this.setState({
            followers_count:event.target.value
        })
    }
    componentDidMount(){
        axios
        .post("http://127.0.0.1:8000/profile/",this.state)
        .then(response=>{
        console.log(response);
        if(response.status="200"){
            this.setState({first_name:response.data["first_name"],
                           last_name:response.data["last_name"],
                           username:response.data["username"],
                           user_bio:response.data["user_bio"],
                           following_count:response.data["following_count"],
                           followers_count:response.data["followers_count"]


           })

        }


    }).catch(error=>{
        if(error.response.status="400"){
            this.setState({error_Message:error.response.data["message"]});
            console.log(error.response);
        }

    })


    }
    tweetshow=()=>{
        axios.post(" http://127.0.0.1:8000/tweets/",this.state).then(response=>{
      console.log(response);
      if(response.status="200"){
          this.setState({tweets:response.data});
      }
        }).catch(error=>{
            console.log(error.response);
        })
        console.log(this.token)
    }

    // Show=()=>{
    //     if(){
    //         document.getElementById("anchortag").style.visibility="visible";
    //     }

    // }


    removeitem(){
        localStorage.removeItem("token");
    }



    render() {
        const {first_name,last_name,username,error_Message,user_bio,following_count,followers_count,tweets}=this.state
        return (
            <div>
            <div className="leftpart">
            <button className="change homebutton"><i class="fa fa-home"></i>Home</button>
            <br></br>
            <br></br>
            <button className="change followersbutton"><i class="fa fa-user-friends"></i>Followers</button>
            <br></br>
            <br></br>
            <button className="change followingbutton"><i class="fa fa-user-plus"></i>Following</button>
            <br></br>
            <br></br>
            <button className="change profilebutton"><i class="fa fa-user"></i>Profile</button>
            <br></br>
            <br></br>
            <button className="tweetbutton">Tweet</button>
            </div>  
            <div className="rightpart">
              <h2 className="styleOfh2">InstaTweets</h2>
              <div className="styleofimg"><img src={Small_logo}></img></div>
             <div style={{height:"0px",width:"102%",borderBottom:"2px solid black",marginTop:"4px",marginLeft:"-1%"}}></div>

        </div>    
           <div className="showprofilediv">
           <h3 >User Profile</h3>
           <center><img src={Usericon} className="userstyle"></img></center>
                <div className="lableStyle"onChange={this.changeFirstName}><label>First Name:<text className="t">{first_name}</text></label></div> <br></br>
             <div className="lableStyle" onChange={this.changeLastName}><label>Last Name: <text className="t">{last_name}</text></label></div> <br></br>
                 <div className="lableStyle" onChange={this.changeUserName} ><label>Username: <text className="t">{username}</text></label></div> <br></br>
                 <div className="lableStyle" onChange={this.changeUserBio} ><label>UserBio: <text className="t">{user_bio}</text></label></div> <br></br>
                 <div className="lableStyle" onChange={this.changeFollowers} ><label>Followers: <text className="t">{followers_count}</text></label></div> <br></br>
                 <div className="lableStyle" onChange={this.changeFollow} ><label>Following: <text className="t">{following_count}</text></label></div><br></br>
                 <center><button type="button" className="biobutton" >Edit Bio</button><br></br></center>
                 <button type="button"  className="buttonstyle" onClick={this.removeitem}>LogOut</button>

               {/* <a id="anchortag" style={{visibility:"hidden"}} onClick={()=>{history.push("/Login")}}>Login</a> */}

             </div>
             <button type='button' onClick={this.tweetshow}>Tweet</button>
                 <div>@{username}</div>{tweets.length? tweets.map(tweet=> <div key={tweet.id}>{tweet.content}<div>{tweet.created_date}</div></div>):null}
                <div><strong>{error_Message}</strong></div>
             </div>

        )
    }
}

export default UserProfile
